import React, { useEffect, useState, useCallback } from 'react';
import { Line, Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';
import '../styles/analytics.css';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Analytics = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [analyticsData, setAnalyticsData] = useState(null);
  const [selectedPeriod, setSelectedPeriod] = useState('30d');

  // Fetch analytics data
  const fetchAnalyticsData = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch(`/api/analytics/summary?period=${selectedPeriod}`);
      if (!response.ok) {
        throw new Error('Failed to fetch analytics data');
      }

      const data = await response.json();
      setAnalyticsData(data);
      setLastUpdated(new Date().toLocaleString('en-IN', { 
        timeZone: 'Asia/Kolkata',
        day: '2-digit',
        month: '2-digit', 
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit'
      }));
    } catch (err) {
      console.error('Analytics fetch error:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, [selectedPeriod]);

  useEffect(() => {
    fetchAnalyticsData();
  }, [fetchAnalyticsData]);

  // Chart configurations
  const getFollowerTrendData = () => {
    if (!analyticsData?.instagram?.follower_trend?.dates?.length) {
      return {
        labels: [],
        datasets: [{
          label: 'Followers',
          data: [],
          borderColor: '#e4405f',
          backgroundColor: 'rgba(228, 64, 95, 0.1)',
          tension: 0.4
        }]
      };
    }

    const { dates, counts } = analyticsData.instagram.follower_trend;
    return {
      labels: dates.map(date => new Date(date).toLocaleDateString()),
      datasets: [{
        label: 'Instagram Followers',
        data: counts,
        borderColor: '#e4405f',
        backgroundColor: 'rgba(228, 64, 95, 0.1)',
        tension: 0.4,
        fill: true
      }]
    };
  };

  const getEngagementComparisonData = () => {
    if (!analyticsData) return { labels: [], datasets: [] };

    const instagramRate = analyticsData.instagram?.avg_engagement_rate || 0;
    const facebookRate = analyticsData.facebook?.avg_engagement_rate || 0;

    return {
      labels: ['Instagram', 'Facebook'],
      datasets: [{
        label: 'Engagement Rate (%)',
        data: [instagramRate, facebookRate],
        backgroundColor: [
          'rgba(228, 64, 95, 0.8)',
          'rgba(24, 119, 242, 0.8)'
        ],
        borderColor: [
          '#e4405f',
          '#1877f2'
        ],
        borderWidth: 2
      }]
    };
  };

  const getBestTimesData = () => {
    const bestTimes = analyticsData?.instagram?.best_times || {};
    const hours = Array.from({ length: 24 }, (_, i) => i);
    const engagementRates = hours.map(hour => 
      bestTimes[hour]?.engagement_rate || 0
    );

    return {
      labels: hours.map(h => `${h.toString().padStart(2, '0')}:00`),
      datasets: [{
        label: 'Engagement Rate (%)',
        data: engagementRates,
        borderColor: '#2563eb',
        backgroundColor: 'rgba(37, 99, 235, 0.1)',
        tension: 0.4,
        fill: true
      }]
    };
  };

  const getPlatformDistributionData = () => {
    if (!analyticsData) return { labels: [], datasets: [] };

    const instagramEngagement = analyticsData.instagram?.total_engagement || 0;
    const facebookEngagement = analyticsData.facebook?.total_engagement || 0;
    const total = instagramEngagement + facebookEngagement;

    if (total === 0) {
      return {
        labels: ['No Data'],
        datasets: [{
          data: [1],
          backgroundColor: ['#e5e7eb']
        }]
      };
    }

    return {
      labels: ['Instagram', 'Facebook'],
      datasets: [{
        data: [instagramEngagement, facebookEngagement],
        backgroundColor: [
          '#e4405f',
          '#1877f2'
        ],
        borderWidth: 2,
        borderColor: '#ffffff'
      }]
    };
  };

  const formatBestTimes = (bestTimes) => {
    if (!bestTimes || Object.keys(bestTimes).length === 0) return [];

    return Object.entries(bestTimes)
      .filter(([_, data]) => data.engagement_rate > 0)
      .sort((a, b) => b[1].engagement_rate - a[1].engagement_rate)
      .slice(0, 5)
      .map(([hour, data]) => ({
        hour: parseInt(hour),
        ...data
      }));
  };

  const formatTime = (hour) => {
    const period = hour >= 12 ? 'PM' : 'AM';
    const displayHour = hour === 0 ? 12 : hour > 12 ? hour - 12 : hour;
    return `${displayHour}:00 ${period}`;
  };

  if (loading) {
    return (
      <div className="analytics-dashboard">
        <div className="analytics-loading">
          <div className="loading-spinner"></div>
          <div className="loading-text">Loading Analytics...</div>
          <div className="loading-subtext">Fetching your social media insights</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="analytics-dashboard">
        <div className="analytics-error">
          <h3>Error Loading Analytics</h3>
          <p>{error}</p>
          <button onClick={fetchAnalyticsData} className="refresh-button">
            Try Again
          </button>
        </div>
      </div>
    );
  }

  const instagramBestTimes = formatBestTimes(analyticsData?.instagram?.best_times);
  const facebookBestTimes = formatBestTimes(analyticsData?.facebook?.best_times);

  return (
    <div className="analytics-dashboard fade-in">
      {/* Header */}
      <div className="mb-8">
        <h1>📊 Analytics Dashboard</h1>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
          <p>Last updated: {lastUpdated} • Analytics will refresh when manually triggered</p>
          <div className="analytics-controls">
            <select 
              value={selectedPeriod} 
              onChange={(e) => setSelectedPeriod(e.target.value)}
              className="period-selector"
            >
              <option value="7d">Last 7 days</option>
              <option value="30d">Last 30 days</option>
              <option value="90d">Last 90 days</option>
            </select>
            <button onClick={fetchAnalyticsData} className="refresh-button">
              🔄 Refresh
            </button>
          </div>
        </div>
      </div>

      {/* Overview Metrics */}
      <div className="grid grid-cols-4 lg:grid-cols-4 md:grid-cols-2 gap-6 mb-8">
        <div className="metric-card slide-up">
          <div className="metric-header">
            <div className="metric-content">
              <h3>Total Followers</h3>
              <div className="metric-value">
                {(analyticsData?.summary?.overview?.total_followers || 0).toLocaleString()}
              </div>
            </div>
            <div className="metric-icon primary">
              👥
            </div>
          </div>
          <div className="metric-footer">
            <div className="metric-stat">
              <span className="metric-label">Growth:</span>
              <span className="metric-value-small">
                {analyticsData?.summary?.overview?.follower_growth || 0}
              </span>
            </div>
          </div>
        </div>

        <div className="metric-card slide-up">
          <div className="metric-header">
            <div className="metric-content">
              <h3>Instagram</h3>
              <div className="metric-value">
                {(analyticsData?.summary?.platforms?.instagram?.followers || 0).toLocaleString()}
              </div>
            </div>
            <div className="metric-icon instagram">
              📸
            </div>
          </div>
          <div className="metric-footer">
            <div className="metric-stat">
              <span className="metric-label">Engagement:</span>
              <span className="metric-value-small">
                {(analyticsData?.summary?.platforms?.instagram?.engagement_rate || 0).toFixed(2)}%
              </span>
            </div>
          </div>
        </div>

        <div className="metric-card slide-up">
          <div className="metric-header">
            <div className="metric-content">
              <h3>Facebook</h3>
              <div className="metric-value">
                {(analyticsData?.summary?.platforms?.facebook?.followers || 0).toLocaleString()}
              </div>
            </div>
            <div className="metric-icon facebook">
              📘
            </div>
          </div>
          <div className="metric-footer">
            <div className="metric-stat">
              <span className="metric-label">Engagement:</span>
              <span className="metric-value-small">
                {(analyticsData?.summary?.platforms?.facebook?.engagement_rate || 0).toFixed(2)}%
              </span>
            </div>
          </div>
        </div>

        <div className="metric-card slide-up">
          <div className="metric-header">
            <div className="metric-content">
              <h3>Total Reach</h3>
              <div className="metric-value">
                {(analyticsData?.summary?.overview?.total_reach || 0).toLocaleString()}
              </div>
            </div>
            <div className="metric-icon success">
              📈
            </div>
          </div>
          <div className="metric-footer">
            <div className="metric-stat">
              <span className="metric-label">Engagements:</span>
              <span className="metric-value-small">
                {(analyticsData?.summary?.overview?.total_engagement || 0).toLocaleString()}
              </span>
            </div>
          </div>
        </div>
      </div>

      {/* Charts Row 1 */}
      <div className="grid grid-cols-2 lg:grid-cols-2 gap-8 mb-8">
        <div className="chart-container hover-lift">
          <h2 className="chart-title">📈 Follower Growth Trend</h2>
          <div className="chart-content">
            <Line 
              data={getFollowerTrendData()} 
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  legend: {
                    position: 'top',
                  },
                },
                scales: {
                  y: {
                    beginAtZero: false,
                  },
                },
              }}
            />
          </div>
        </div>

        <div className="chart-container hover-lift">
          <h2 className="chart-title">💬 Engagement Comparison</h2>
          <div className="chart-content">
            <Bar 
              data={getEngagementComparisonData()} 
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  legend: {
                    position: 'top',
                  },
                },
                scales: {
                  y: {
                    beginAtZero: true,
                  },
                },
              }}
            />
          </div>
        </div>
      </div>

      {/* Charts Row 2 */}
      <div className="grid grid-cols-2 lg:grid-cols-2 gap-8 mb-8">
        <div className="chart-container hover-lift">
          <h2 className="chart-title">⏰ Best Times to Post</h2>
          <div className="chart-content">
            <Line 
              data={getBestTimesData()} 
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  legend: {
                    position: 'top',
                  },
                },
                scales: {
                  y: {
                    beginAtZero: true,
                  },
                },
              }}
            />
          </div>
        </div>

        <div className="chart-container hover-lift">
          <h2 className="chart-title">🏆 Platform Distribution</h2>
          <div className="chart-content">
            <Doughnut 
              data={getPlatformDistributionData()} 
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  legend: {
                    position: 'bottom',
                  },
                },
              }}
            />
          </div>
        </div>
      </div>

      {/* Platform Insights */}
      <div className="grid grid-cols-2 lg:grid-cols-2 gap-8 mb-8">
        <div className="platform-insights hover-lift">
          <div className="platform-header instagram-gradient">
            <h3>📊 Instagram Performance</h3>
          </div>
          <div className="platform-metrics">
            <div className="metric-row">
              <span className="metric-label">Followers</span>
              <span className="metric-value">{(analyticsData?.instagram?.current_followers || 0).toLocaleString()}</span>
            </div>
            <div className="metric-row">
              <span className="metric-label">Engagement Rate</span>
              <span className="metric-value">{(analyticsData?.instagram?.avg_engagement_rate || 0).toFixed(2)}%</span>
            </div>
            <div className="metric-row">
              <span className="metric-label">Total Engagement</span>
              <span className="metric-value">{(analyticsData?.instagram?.total_engagement || 0).toLocaleString()}</span>
            </div>
            <div className="metric-row">
              <span className="metric-label">Reach</span>
              <span className="metric-value">{(analyticsData?.instagram?.total_reach || 0).toLocaleString()}</span>
            </div>
            <div className="metric-row">
              <span className="metric-label">Growth Rate</span>
              <span className="metric-value">0.0%</span>
            </div>
          </div>
        </div>

        <div className="platform-insights hover-lift">
          <div className="platform-header facebook-gradient">
            <h3>📊 Facebook Performance</h3>
          </div>
          <div className="platform-metrics">
            <div className="metric-row">
              <span className="metric-label">Followers</span>
              <span className="metric-value">{(analyticsData?.facebook?.follower_trend?.counts?.slice(-1)[0] || 0).toLocaleString()}</span>
            </div>
            <div className="metric-row">
              <span className="metric-label">Engagement Rate</span>
              <span className="metric-value">{(analyticsData?.facebook?.avg_engagement_rate || 0).toFixed(2)}%</span>
            </div>
            <div className="metric-row">
              <span className="metric-label">Total Engagement</span>
              <span className="metric-value">{(analyticsData?.facebook?.total_engagement || 0).toLocaleString()}</span>
            </div>
            <div className="metric-row">
              <span className="metric-label">Reach</span>
              <span className="metric-value">{(analyticsData?.facebook?.total_reach || 0).toLocaleString()}</span>
            </div>
            <div className="metric-row">
              <span className="metric-label">Growth Rate</span>
              <span className="metric-value">0.0%</span>
            </div>
          </div>
        </div>
      </div>

      {/* Best Times */}
      <div className="grid grid-cols-2 lg:grid-cols-2 gap-8 mb-8">
        <div className="best-times-card hover-lift">
          <h3 className="best-times-title instagram-text">🕐 Instagram Best Times</h3>
          <div className="best-times-list">
            {instagramBestTimes.length > 0 ? (
              instagramBestTimes.map((time, index) => (
                <div key={index} className="best-time-item">
                  <div className="time-info">
                    <span className="time-hour">{formatTime(time.hour)}</span>
                  </div>
                  <div className="time-stats">
                    <span className="engagement-rate">{time.engagement_rate.toFixed(1)}%</span>
                    <span className="post-count">({time.post_count} posts)</span>
                  </div>
                </div>
              ))
            ) : (
              <div className="no-data">No data available</div>
            )}
          </div>
        </div>

        <div className="best-times-card hover-lift">
          <h3 className="best-times-title facebook-text">🕐 Facebook Best Times</h3>
          <div className="best-times-list">
            {facebookBestTimes.length > 0 ? (
              facebookBestTimes.map((time, index) => (
                <div key={index} className="best-time-item">
                  <div className="time-info">
                    <span className="time-hour">{formatTime(time.hour)}</span>
                  </div>
                  <div className="time-stats">
                    <span className="engagement-rate">{time.engagement_rate.toFixed(1)}%</span>
                    <span className="post-count">({time.post_count} posts)</span>
                  </div>
                </div>
              ))
            ) : (
              <div className="no-data">No data available</div>
            )}
          </div>
        </div>
      </div>

      {/* Top Posts */}
      <div className="grid grid-cols-2 lg:grid-cols-2 gap-8 mb-8">
        <div className="top-posts-card hover-lift">
          <h3 className="top-posts-title instagram-text">🎯 Instagram Top Posts</h3>
          <div className="top-posts-list">
            {analyticsData?.instagram?.top_posts?.length > 0 ? (
              analyticsData.instagram.top_posts.slice(0, 3).map((post, index) => (
                <div key={index} className="top-post-item">
                  <div className="post-rank">{index + 1}</div>
                  <div className="post-info">
                    <div className="post-caption">
                      {post.caption ? post.caption.substring(0, 100) + '...' : 'No caption'}
                    </div>
                    <div className="post-metrics">
                      <span className="metric">❤️ {(post.likes || 0).toLocaleString()}</span>
                      <span className="metric">💬 {(post.comments || 0).toLocaleString()}</span>
                      <span className="metric">📊 {(post.engagement_rate || 0).toFixed(1)}%</span>
                    </div>
                    {post.permalink_url && (
                      <a href={post.permalink_url} target="_blank" rel="noopener noreferrer" className="view-post-link">
                        View Post →
                      </a>
                    )}
                  </div>
                </div>
              ))
            ) : (
              <div className="no-data">No posts available</div>
            )}
          </div>
        </div>

        <div className="top-posts-card hover-lift">
          <h3 className="top-posts-title facebook-text">🎯 Facebook Top Posts</h3>
          <div className="top-posts-list">
            {analyticsData?.facebook?.top_posts?.length > 0 ? (
              analyticsData.facebook.top_posts.slice(0, 3).map((post, index) => (
                <div key={index} className="top-post-item">
                  <div className="post-rank">{index + 1}</div>
                  <div className="post-info">
                    <div className="post-caption">
                      {post.message ? post.message.substring(0, 100) + '...' : 'No message'}
                    </div>
                    <div className="post-metrics">
                      <span className="metric">👍 {(post.reactions || 0).toLocaleString()}</span>
                      <span className="metric">💬 {(post.comments || 0).toLocaleString()}</span>
                      <span className="metric">📊 {(post.engagement_rate || 0).toFixed(1)}%</span>
                    </div>
                    {post.permalink_url && (
                      <a href={post.permalink_url} target="_blank" rel="noopener noreferrer" className="view-post-link">
                        View Post →
                      </a>
                    )}
                  </div>
                </div>
              ))
            ) : (
              <div className="no-data">No posts available</div>
            )}
          </div>
        </div>
      </div>

      {/* Growth Comparison Summary */}
      <div className="growth-summary-card hover-lift">
        <h3 className="growth-summary-title">📈 Growth Comparison Summary</h3>
        <div className="growth-comparison-grid">
          <div className="growth-metric">
            <span className="growth-label">Instagram Growth</span>
            <div className="growth-value">0.0%</div>
          </div>
          <div className="growth-metric">
            <span className="growth-label">Facebook Growth</span>
            <div className="growth-value">0.0%</div>
          </div>
          <div className="growth-metric">
            <span className="growth-label">Better Platform</span>
            <div className="growth-value">
              {(analyticsData?.instagram?.avg_engagement_rate || 0) >= (analyticsData?.facebook?.avg_engagement_rate || 0) 
                ? '📸 Instagram' 
                : '📘 Facebook'}
            </div>
          </div>
          <div className="growth-metric">
            <span className="growth-label">Growth Difference</span>
            <div className="growth-value">0.0%</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Analytics;
