import React, { useState } from 'react';
import './ContentCalendar.css';

const ContentCalendar = () => {
  const [selectedMonth, setSelectedMonth] = useState(new Date().getMonth() + 1);
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [numDays, setNumDays] = useState(14);
  const [foodStyle, setFoodStyle] = useState(['Both']);
  const [promotionFocus, setPromotionFocus] = useState('');
  const [generatedCalendar, setGeneratedCalendar] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const months = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
  ];

  const foodStyles = ['South Indian', 'North Indian', 'Both'];
  const currentYear = new Date().getFullYear();
  const years = [currentYear, currentYear + 1, currentYear + 2];

  const handleFoodStyleChange = (style) => {
    setFoodStyle(prev => {
      if (prev.includes(style)) {
        return prev.filter(s => s !== style);
      } else {
        return [...prev, style];
      }
    });
  };

  const generateCalendar = async () => {
    if (foodStyle.length === 0) {
      setError('Please select at least one food style');
      return;
    }

    setIsLoading(true);
    setError('');

    try {
      const response = await fetch('/api/generate-calendar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          month: selectedMonth,
          year: selectedYear,
          num_days: numDays,
          food_style: foodStyle,
          promotion_focus: promotionFocus,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to generate calendar');
      }

      const data = await response.json();
      setGeneratedCalendar(data.calendar);
    } catch (err) {
      setError('Error generating content calendar: ' + err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const downloadCSV = () => {
    if (!generatedCalendar) return;

    const csvContent = [
      ['Date', 'Topic', 'Post Idea', 'Instagram Feature', 'Hashtags'],
      ...generatedCalendar.map(item => [
        item.date,
        item.topic,
        item.post_idea,
        item.instagram_feature,
        item.hashtags
      ])
    ].map(row => row.map(field => `"${field}"`).join(',')).join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `content_calendar_${months[selectedMonth - 1]}_${selectedYear}.csv`;
    link.click();
    window.URL.revokeObjectURL(url);
  };

  const getInstagramFeatureIcon = (feature) => {
    switch (feature?.toLowerCase()) {
      case 'reel':
        return '🎬';
      case 'story':
        return '📱';
      case 'carousel':
        return '🔄';
      case 'static post':
        return '📸';
      default:
        return '📝';
    }
  };

  const getInstagramFeatureColor = (feature) => {
    switch (feature?.toLowerCase()) {
      case 'reel':
        return '#FF3040';
      case 'story':
        return '#833AB4';
      case 'carousel':
        return '#405DE6';
      case 'static post':
        return '#C13584';
      default:
        return '#666';
    }
  };

  return (
    <div className="content-calendar">
      <div className="calendar-header">
        <h1>🗓️ AI Content Calendar Generator</h1>
        <p>Generate a customized content calendar for your food delivery service</p>
      </div>

      <div className="calendar-form">
        <div className="form-grid">
          <div className="form-group">
            <label>Select Month</label>
            <select 
              value={selectedMonth} 
              onChange={(e) => setSelectedMonth(parseInt(e.target.value))}
              className="form-select"
            >
              {months.map((month, index) => (
                <option key={month} value={index + 1}>{month}</option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Select Year</label>
            <select 
              value={selectedYear} 
              onChange={(e) => setSelectedYear(parseInt(e.target.value))}
              className="form-select"
            >
              {years.map(year => (
                <option key={year} value={year}>{year}</option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Number of Days</label>
            <input 
              type="number" 
              min="7" 
              max="31" 
              value={numDays}
              onChange={(e) => setNumDays(parseInt(e.target.value))}
              className="form-input"
            />
          </div>
        </div>

        <div className="form-group">
          <label>Food Style(s)</label>
          <div className="checkbox-group">
            {foodStyles.map(style => (
              <label key={style} className="checkbox-label">
                <input
                  type="checkbox"
                  checked={foodStyle.includes(style)}
                  onChange={() => handleFoodStyleChange(style)}
                />
                <span className="checkbox-text">{style}</span>
              </label>
            ))}
          </div>
        </div>

        <div className="form-group">
          <label>Promotion Focus (Optional)</label>
          <textarea
            value={promotionFocus}
            onChange={(e) => setPromotionFocus(e.target.value)}
            placeholder="e.g., 'summer specials', 'healthy options', 'festive deals'"
            className="form-textarea"
          />
        </div>

        <button 
          onClick={generateCalendar}
          disabled={isLoading || foodStyle.length === 0}
          className="generate-btn"
        >
          {isLoading ? (
            <>
              <div className="spinner"></div>
              Generating Calendar...
            </>
          ) : (
            <>
              ✨ Generate Content Calendar
            </>
          )}
        </button>

        {error && <div className="error-message">{error}</div>}
      </div>

      {generatedCalendar && (
        <div className="calendar-results">
          <div className="results-header">
            <h2>🎯 Generated Content Calendar</h2>
            <button onClick={downloadCSV} className="download-btn">
              📥 Download CSV
            </button>
          </div>

          <div className="calendar-grid">
            {generatedCalendar.map((item, index) => (
              <div key={index} className="calendar-card">
                <div className="card-header">
                  <div className="card-date">
                    {new Date(item.date).toLocaleDateString('en-US', {
                      month: 'short',
                      day: 'numeric'
                    })}
                  </div>
                  <div 
                    className="card-feature"
                    style={{ backgroundColor: getInstagramFeatureColor(item.instagram_feature) }}
                  >
                    {getInstagramFeatureIcon(item.instagram_feature)} {item.instagram_feature}
                  </div>
                </div>

                <div className="card-content">
                  <h3 className="card-topic">{item.topic}</h3>
                  <p className="card-idea">{item.post_idea}</p>
                  <div className="card-hashtags">{item.hashtags}</div>
                </div>
              </div>
            ))}
          </div>

          <div className="calendar-stats">
            <div className="stat">
              <span className="stat-number">{generatedCalendar.length}</span>
              <span className="stat-label">Total Posts</span>
            </div>
            <div className="stat">
              <span className="stat-number">
                {generatedCalendar.filter(item => item.instagram_feature?.toLowerCase() === 'reel').length}
              </span>
              <span className="stat-label">Reels</span>
            </div>
            <div className="stat">
              <span className="stat-number">
                {generatedCalendar.filter(item => item.instagram_feature?.toLowerCase() === 'carousel').length}
              </span>
              <span className="stat-label">Carousels</span>
            </div>
            <div className="stat">
              <span className="stat-number">
                {generatedCalendar.filter(item => item.instagram_feature?.toLowerCase() === 'story').length}
              </span>
              <span className="stat-label">Stories</span>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ContentCalendar;
