import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { 
  CalendarDaysIcon, 
  ChartBarIcon, 
  ClockIcon, 
  PhotoIcon
} from '@heroicons/react/24/outline';
import apiClient from '../api';
import { formatLocalDateTime } from '../utils/dateFormatter';

const Dashboard = () => {
  const [stats, setStats] = useState({
    posts: [],
    analytics: {
      instagram: null,
      facebook: null
    },
    isLoading: true
  });

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        // Fetch posts
        const postsResponse = await apiClient.getPosts();
        console.log('Posts response:', postsResponse);

        // Fetch analytics
        const analyticsResponse = await apiClient.getAnalytics();
        console.log('Analytics response:', analyticsResponse);

        setStats({
          posts: postsResponse.posts || [],
          analytics: {
            instagram: analyticsResponse.data?.instagram || null,
            facebook: analyticsResponse.data?.facebook || null
          },
          isLoading: false
        });
      } catch (error) {
        console.error('Error loading dashboard data:', error);
        setStats(prev => ({ ...prev, isLoading: false }));
      }
    };

    fetchDashboardData();
  }, []);

  // Extract metrics with safer access
  const instagramFollowers = stats.analytics.instagram?.total_followers_gained || 
                           stats.analytics.instagram?.follower_trend?.counts?.[stats.analytics.instagram?.follower_trend?.counts?.length - 1] || 0;

  const instagramEngagement = stats.analytics.instagram?.avg_engagement_rate?.toFixed(2) || '0.00';
  const instagramReach = stats.analytics.instagram?.total_reach || 0;

  const facebookFollowers = stats.analytics.facebook?.total_followers_gained || 
                          stats.analytics.facebook?.growth_metrics?.total_followers_gained || 0;

  const facebookEngagement = stats.analytics.facebook?.engagement_rate?.toFixed(2) || 
                           stats.analytics.facebook?.growth_metrics?.engagement_rate?.toFixed(2) || '0.00';

  const facebookReach = stats.analytics.facebook?.total_reach || 
                       stats.analytics.facebook?.growth_metrics?.total_reach || 0;

  const scheduledPosts = stats.posts.filter(post => post.status === 'scheduled').length;
  const totalPosts = stats.posts.length;
  const recentPosts = stats.posts
    .sort((a, b) => new Date(b.scheduled_time) - new Date(a.scheduled_time))
    .slice(0, 3);

  return (
    <div className="dashboard">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
        <div className="card">
          <h2 className="text-2xl font-bold mb-4">Instagram Insights</h2>
          <div className="grid grid-cols-3 gap-4">
            <div className="stat-card">
              <div className="stat-value">{instagramFollowers}</div>
              <div className="stat-label">Followers Gained</div>
      </div>
            <div className="stat-card">
              <div className="stat-value">{instagramEngagement}%</div>
              <div className="stat-label">Engagement Rate</div>
          </div>
            <div className="stat-card">
              <div className="stat-value">{instagramReach}</div>
              <div className="stat-label">Total Reach</div>
        </div>
          </div>
        </div>

        <div className="card">
          <h2 className="text-2xl font-bold mb-4">Facebook Insights</h2>
          <div className="grid grid-cols-3 gap-4">
            <div className="stat-card">
              <div className="stat-value">{facebookFollowers}</div>
              <div className="stat-label">Followers Gained</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{facebookEngagement}%</div>
              <div className="stat-label">Engagement Rate</div>
          </div>
            <div className="stat-card">
              <div className="stat-value">{facebookReach}</div>
              <div className="stat-label">Total Reach</div>
        </div>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="card col-span-1">
          <h2 className="text-2xl font-bold mb-4">Posts Overview</h2>
          <div className="grid grid-cols-2 gap-4">
            <div className="stat-card">
              <div className="stat-value">{scheduledPosts}</div>
              <div className="stat-label">Scheduled</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{totalPosts - scheduledPosts}</div>
              <div className="stat-label">Published</div>
            </div>
          </div>
        </div>

        <div className="card col-span-2">
          <h2 className="text-2xl font-bold mb-4">Recent Posts</h2>
          {stats.isLoading ? (
            <div className="text-center p-4">Loading...</div>
          ) : recentPosts.length > 0 ? (
            <div className="space-y-4">
              {recentPosts.map((post, index) => (
                <div key={post.id || `post-${index}`} className="flex items-center p-2 border-b">
                  {post.media_urls && post.media_urls[0] && (
                    <img 
                      src={post.media_urls[0]} 
                      alt="Post" 
                      className="w-12 h-12 object-cover rounded mr-4"
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = 'https://via.placeholder.com/48?text=Media';
                      }}
                    />
                  )}
                  <div className="flex-1">
                    <div className="text-sm font-medium">{post.caption?.substring(0, 60)}...</div>
                    <div className="text-xs text-gray-500">
                      {formatLocalDateTime(post.scheduled_time)} Â· {post.platforms || post.platform || 'Instagram'}
                    </div>
                  </div>
                  <div className={`text-xs px-2 py-1 rounded-full ${
                    post.status === 'scheduled' ? 'bg-blue-100 text-blue-800' : 
                    post.status === 'posted' ? 'bg-green-100 text-green-800' : 
                    'bg-gray-100 text-gray-800'
                  }`}>
                    {post.status}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center p-4 text-gray-500">No recent posts</div>
          )}
        </div>
                </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <Link to="/scheduler" className="card hover:shadow-md transition-all">
          <div className="text-primary-600 mb-4">
            <PhotoIcon className="h-8 w-8" />
              </div>
          <h3 className="text-lg font-bold mb-2">Create Post</h3>
          <p className="text-sm text-gray-600">Upload and schedule new content</p>
        </Link>

        <Link to="/scheduled" className="card hover:shadow-md transition-all">
          <div className="text-blue-600 mb-4">
            <ClockIcon className="h-8 w-8" />
          </div>
          <h3 className="text-lg font-bold mb-2">Scheduled Posts</h3>
          <p className="text-sm text-gray-600">View and manage upcoming posts</p>
        </Link>

        <Link to="/analytics" className="card hover:shadow-md transition-all">
          <div className="text-green-600 mb-4">
            <ChartBarIcon className="h-8 w-8" />
        </div>
          <h3 className="text-lg font-bold mb-2">Analytics</h3>
          <p className="text-sm text-gray-600">Track performance metrics</p>
        </Link>

        <Link to="/calendar" className="card hover:shadow-md transition-all">
          <div className="text-purple-600 mb-4">
            <CalendarDaysIcon className="h-8 w-8" />
        </div>
          <h3 className="text-lg font-bold mb-2">Content Calendar</h3>
          <p className="text-sm text-gray-600">Plan your content strategy</p>
        </Link>
      </div>
    </div>
  );
};

export default Dashboard;
