import React, { useEffect, useState, useCallback } from 'react';
import { Line, Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';
import '../styles/analytics.css';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Analytics = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [analyticsData, setAnalyticsData] = useState(null);
  const [selectedPeriod, setSelectedPeriod] = useState('30d');

  // Fetch analytics data
  const fetchAnalyticsData = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch(`/api/analytics/summary?period=${selectedPeriod}`);
      if (!response.ok) {
        throw new Error('Failed to fetch analytics data');
      }

      const data = await response.json();
      setAnalyticsData(data);
      setLastUpdated(new Date().toLocaleString('en-IN', { 
        timeZone: 'Asia/Kolkata',
        day: '2-digit',
        month: '2-digit', 
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit'
      }));
    } catch (err) {
      console.error('Analytics fetch error:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, [selectedPeriod]);

  useEffect(() => {
    fetchAnalyticsData();
  }, [fetchAnalyticsData]);

  // Chart configurations with proper styling
  const getFollowerTrendData = () => {
    const datasets = [];
    let labels = [];

    // Instagram data
    if (analyticsData?.instagram?.follower_trend?.dates?.length) {
      const { dates, counts } = analyticsData.instagram.follower_trend;
      labels = dates.map(date => new Date(date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' }));
      datasets.push({
        label: 'Instagram Followers',
        data: counts,
        borderColor: '#e4405f',
        backgroundColor: 'rgba(228, 64, 95, 0.1)',
        tension: 0.4,
        fill: false,
        pointBackgroundColor: '#e4405f',
        pointBorderColor: '#ffffff',
        pointBorderWidth: 2,
        pointRadius: 4
      });
    }

    // Facebook data
    if (analyticsData?.facebook?.follower_trend?.dates?.length) {
      const { dates, counts } = analyticsData.facebook.follower_trend;
      if (labels.length === 0) {
        labels = dates.map(date => new Date(date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' }));
      }
      datasets.push({
        label: 'Facebook Followers',
        data: counts,
        borderColor: '#1877f2',
        backgroundColor: 'rgba(24, 119, 242, 0.1)',
        tension: 0.4,
        fill: false,
        pointBackgroundColor: '#1877f2',
        pointBorderColor: '#ffffff',
        pointBorderWidth: 2,
        pointRadius: 4
      });
    }

    // Fallback if no data
    if (datasets.length === 0) {
      return {
        labels: ['No Data'],
        datasets: [{
          label: 'No Data Available',
          data: [0],
          borderColor: '#e5e7eb',
          backgroundColor: 'rgba(229, 231, 235, 0.1)',
          tension: 0.4
        }]
      };
    }

    return { labels, datasets };
  };

  const getEngagementComparisonData = () => {
    if (!analyticsData) return { labels: ['No Data'], datasets: [{ data: [0], backgroundColor: ['#e5e7eb'] }] };

    const instagramRate = analyticsData.instagram?.avg_engagement_rate || 0;
    const facebookRate = analyticsData.facebook?.avg_engagement_rate || 0;

    return {
      labels: ['Instagram', 'Facebook'],
      datasets: [{
        label: 'Engagement Rate (%)',
        data: [instagramRate, facebookRate],
        backgroundColor: [
          'rgba(228, 64, 95, 0.8)',
          'rgba(24, 119, 242, 0.8)'
        ],
        borderColor: [
          '#e4405f',
          '#1877f2'
        ],
        borderWidth: 2,
        borderRadius: 8
      }]
    };
  };

  const getBestTimesData = () => {
    const instagramBestTimes = analyticsData?.instagram?.best_times || {};
    const facebookBestTimes = analyticsData?.facebook?.best_times || {};
    const hours = Array.from({ length: 24 }, (_, i) => i);

    const instagramRates = hours.map(hour => instagramBestTimes[hour]?.engagement_rate || 0);
    const facebookRates = hours.map(hour => facebookBestTimes[hour]?.engagement_rate || 0);

    return {
      labels: hours.map(h => `${h.toString().padStart(2, '0')}:00`),
      datasets: [
        {
          label: 'Instagram Engagement (%)',
          data: instagramRates,
          borderColor: '#e4405f',
          backgroundColor: 'rgba(228, 64, 95, 0.1)',
          tension: 0.4,
          fill: false,
          pointBackgroundColor: '#e4405f',
          pointBorderColor: '#ffffff',
          pointBorderWidth: 2,
          pointRadius: 3
        },
        {
          label: 'Facebook Engagement (%)',
          data: facebookRates,
          borderColor: '#1877f2',
          backgroundColor: 'rgba(24, 119, 242, 0.1)',
          tension: 0.4,
          fill: false,
          pointBackgroundColor: '#1877f2',
          pointBorderColor: '#ffffff',
          pointBorderWidth: 2,
          pointRadius: 3
        }
      ]
    };
  };

  const getPlatformDistributionData = () => {
    if (!analyticsData) return { labels: ['No Data'], datasets: [{ data: [1], backgroundColor: ['#e5e7eb'] }] };

    const instagramEngagement = analyticsData.instagram?.total_engagement || 0;
    const facebookEngagement = analyticsData.facebook?.total_engagement || 0;
    const total = instagramEngagement + facebookEngagement;

    if (total === 0) {
      return {
        labels: ['No Data Available'],
        datasets: [{
          data: [1],
          backgroundColor: ['#e5e7eb'],
          borderWidth: 0
        }]
      };
    }

    return {
      labels: ['Instagram', 'Facebook'],
      datasets: [{
        data: [instagramEngagement, facebookEngagement],
        backgroundColor: [
          '#e4405f',
          '#1877f2'
        ],
        borderWidth: 3,
        borderColor: '#ffffff',
        hoverBorderWidth: 4
      }]
    };
  };

  const formatBestTimes = (bestTimes) => {
    if (!bestTimes || Object.keys(bestTimes).length === 0) return [];

    return Object.entries(bestTimes)
      .filter(([_, data]) => data.engagement_rate > 0)
      .sort((a, b) => b[1].engagement_rate - a[1].engagement_rate)
      .slice(0, 5)
      .map(([hour, data]) => ({
        hour: parseInt(hour),
        ...data
      }));
  };

  const formatTime = (hour) => {
    const period = hour >= 12 ? 'PM' : 'AM';
    const displayHour = hour === 0 ? 12 : hour > 12 ? hour - 12 : hour;
    return `${displayHour}:00 ${period}`;
  };

  if (loading) {
    return (
      <div className="analytics-dashboard">
        <div className="analytics-loading">
          <div className="loading-spinner"></div>
          <div className="loading-text">Loading Analytics...</div>
          <div className="loading-subtext">Fetching your social media insights</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="analytics-dashboard">
        <div className="analytics-error">
          <h3>⚠️ Error Loading Analytics</h3>
          <p>{error}</p>
          <button onClick={fetchAnalyticsData} className="refresh-button">
            🔄 Try Again
          </button>
        </div>
      </div>
    );
  }

  const instagramBestTimes = formatBestTimes(analyticsData?.instagram?.best_times);
  const facebookBestTimes = formatBestTimes(analyticsData?.facebook?.best_times);

  return (
    <div className="analytics-dashboard fade-in">
      {/* Header Section */}
      <div className="analytics-header">
        <div className="header-content">
          <h1>📊 Analytics Dashboard</h1>
          <p>Last updated: {lastUpdated} • Analytics will refresh when manually triggered</p>
        </div>
        <div className="analytics-controls">
          <select 
            value={selectedPeriod} 
            onChange={(e) => setSelectedPeriod(e.target.value)}
            className="period-selector"
          >
            <option value="7d">Last 7 days</option>
            <option value="30d">Last 30 days</option>
            <option value="90d">Last 90 days</option>
          </select>
          <button onClick={fetchAnalyticsData} className="refresh-button">
            🔄 Refresh
          </button>
        </div>
      </div>

      {/* Overview Metrics - Properly Aligned Grid */}
      <div className="metrics-section">
        <div className="metrics-grid">
          <div className="metric-card">
            <div className="metric-content">
              <div className="metric-info">
                <h3>Total Followers</h3>
                <div className="metric-value">
                  {(analyticsData?.summary?.overview?.total_followers || 0).toLocaleString()}
                </div>
                <div className="metric-change">
                  <span className="change-label">Growth:</span>
                  <span className="change-value">
                    {analyticsData?.summary?.overview?.follower_growth || 0}
                  </span>
                </div>
              </div>
              <div className="metric-icon primary">
                <svg width="24" height="24" fill="currentColor" viewBox="0 0 24 24">
                  <path d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"/>
                </svg>
              </div>
            </div>
          </div>

          <div className="metric-card">
            <div className="metric-content">
              <div className="metric-info">
                <h3>Instagram</h3>
                <div className="metric-value">
                  {(analyticsData?.summary?.platforms?.instagram?.followers || 0).toLocaleString()}
                </div>
                <div className="metric-change">
                  <span className="change-label">Engagement:</span>
                  <span className="change-value">
                    {(analyticsData?.summary?.platforms?.instagram?.engagement_rate || 0).toFixed(2)}%
                  </span>
                </div>
              </div>
              <div className="metric-icon instagram">
                <svg width="24" height="24" fill="currentColor" viewBox="0 0 24 24">
                  <path d="M12 2.163c3.204 0 3.584.012 4.85.07 3.252.148 4.771 1.691 4.919 4.919.058 1.265.069 1.645.069 4.849 0 3.205-.012 3.584-.069 4.849-.149 3.225-1.664 4.771-4.919 4.919-1.266.058-1.644.07-4.85.07-3.204 0-3.584-.012-4.849-.07-3.26-.149-4.771-1.699-4.919-4.92-.058-1.265-.07-1.644-.07-4.849 0-3.204.013-3.583.07-4.849.149-3.227 1.664-4.771 4.919-4.919 1.266-.057 1.645-.069 4.849-.069zm0-2.163c-3.259 0-3.667.014-4.947.072-4.358.2-6.78 2.618-6.98 6.98-.059 1.281-.073 1.689-.073 4.948 0 3.259.014 3.668.072 4.948.2 4.358 2.618 6.78 6.98 6.98 1.281.058 1.689.072 4.948.072 3.259 0 3.668-.014 4.948-.072 4.354-.2 6.782-2.618 6.979-6.98.059-1.28.073-1.689.073-4.948 0-3.259-.014-3.667-.072-4.947-.196-4.354-2.617-6.78-6.979-6.98-1.281-.059-1.69-.073-4.949-.073zm0 5.838c-3.403 0-6.162 2.759-6.162 6.162s2.759 6.163 6.162 6.163 6.162-2.759 6.162-6.163c0-3.403-2.759-6.162-6.162-6.162zm0 10.162c-2.209 0-4-1.79-4-4 0-2.209 1.791-4 4-4s4 1.791 4 4c0 2.21-1.791 4-4 4zm6.406-11.845c-.796 0-1.441.645-1.441 1.44s.645 1.44 1.441 1.44c.795 0 1.439-.645 1.439-1.44s-.644-1.44-1.439-1.44z"/>
                </svg>
              </div>
            </div>
          </div>

          <div className="metric-card">
            <div className="metric-content">
              <div className="metric-info">
                <h3>Facebook</h3>
                <div className="metric-value">
                  {(analyticsData?.summary?.platforms?.facebook?.followers || 0).toLocaleString()}
                </div>
                <div className="metric-change">
                  <span className="change-label">Engagement:</span>
                  <span className="change-value">
                    {(analyticsData?.summary?.platforms?.facebook?.engagement_rate || 0).toFixed(2)}%
                  </span>
                </div>
              </div>
              <div className="metric-icon facebook">
                <svg width="24" height="24" fill="currentColor" viewBox="0 0 24 24">
                  <path d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z"/>
                </svg>
              </div>
            </div>
          </div>

          <div className="metric-card">
            <div className="metric-content">
              <div className="metric-info">
                <h3>Total Reach</h3>
                <div className="metric-value">
                  {(analyticsData?.summary?.overview?.total_reach || 0).toLocaleString()}
                </div>
                <div className="metric-change">
                  <span className="change-label">Engagements:</span>
                  <span className="change-value">
                    {(analyticsData?.summary?.overview?.total_engagement || 0).toLocaleString()}
                  </span>
        </div>
            </div>
              <div className="metric-icon success">
                <svg width="24" height="24" fill="currentColor" viewBox="0 0 24 24">
                  <path d="M2.01 21L23 12 2.01 3 2 10l15 2-15 2z"/>
                </svg>
            </div>
            </div>
          </div>
        </div>
      </div>

      {/* Charts Section - Row 1 */}
      <div className="charts-section">
        <div className="charts-grid">
        <div className="chart-container">
            <div className="chart-header">
              <h3>📈 Follower Growth Trend</h3>
            </div>
            <div className="chart-content">
              <Line 
                data={getFollowerTrendData()} 
                options={{
              responsive: true,
                  maintainAspectRatio: false,
              plugins: {
                legend: {
                  position: 'top',
                      labels: {
                        usePointStyle: true,
                        padding: 20
                      }
                },
                tooltip: {
                      backgroundColor: 'rgba(0, 0, 0, 0.8)',
                      titleColor: '#ffffff',
                      bodyColor: '#ffffff',
                      borderColor: '#e4405f',
                      borderWidth: 1
                    }
                  },
                  scales: {
                    x: {
                      grid: {
                        display: false
                      }
                    },
                    y: {
                      beginAtZero: false,
                      grid: {
                        color: 'rgba(0, 0, 0, 0.1)'
                      }
                    }
                  }
                }}
              />
            </div>
          </div>

          <div className="chart-container">
            <div className="chart-header">
              <h3>💬 Engagement Comparison</h3>
            </div>
            <div className="chart-content">
              <Bar 
                data={getEngagementComparisonData()} 
                options={{
                  responsive: true,
                  maintainAspectRatio: false,
                  plugins: {
                    legend: {
                      display: false
                    },
                    tooltip: {
                      backgroundColor: 'rgba(0, 0, 0, 0.8)',
                      titleColor: '#ffffff',
                      bodyColor: '#ffffff'
                }
              },
              scales: {
                    x: {
                      grid: {
                        display: false
                      }
                    },
                y: {
                  beginAtZero: true,
                      grid: {
                        color: 'rgba(0, 0, 0, 0.1)'
                      }
                    }
                  }
                }}
              />
            </div>
          </div>
        </div>
        </div>

      {/* Charts Section - Row 2 */}
      <div className="charts-section">
        <div className="charts-grid">
        <div className="chart-container">
            <div className="chart-header">
              <h3>⏰ Best Times to Post</h3>
            </div>
            <div className="chart-content">
              <Line 
                data={getBestTimesData()} 
                options={{
              responsive: true,
                  maintainAspectRatio: false,
              plugins: {
                legend: {
                  position: 'top',
                      labels: {
                        usePointStyle: true,
                        padding: 20
                      }
                    }
                  },
                  scales: {
                    x: {
                      grid: {
                        display: false
                      }
                    },
                y: {
                  beginAtZero: true,
                      grid: {
                        color: 'rgba(0, 0, 0, 0.1)'
                      }
                    }
                  }
                }}
              />
            </div>
        </div>

        <div className="chart-container">
            <div className="chart-header">
              <h3>🏆 Platform Distribution</h3>
            </div>
            <div className="chart-content">
              <Doughnut 
                data={getPlatformDistributionData()} 
                options={{
              responsive: true,
              maintainAspectRatio: false,
              plugins: {
                legend: {
                  position: 'bottom',
                      labels: {
                        padding: 20,
                        usePointStyle: true
                      }
                    }
                  }
                }}
              />
            </div>
          </div>
        </div>
      </div>

      {/* Platform Insights Section */}
      <div className="insights-section">
        <div className="insights-grid">
          <div className="platform-card instagram-card">
            <div className="platform-header">
              <div className="platform-title">
                <svg width="20" height="20" fill="currentColor" viewBox="0 0 24 24">
                  <path d="M12 2.163c3.204 0 3.584.012 4.85.07 3.252.148 4.771 1.691 4.919 4.919.058 1.265.069 1.645.069 4.849 0 3.205-.012 3.584-.069 4.849-.149 3.225-1.664 4.771-4.919 4.919-1.266.058-1.644.07-4.85.07-3.204 0-3.584-.012-4.849-.07-3.26-.149-4.771-1.699-4.919-4.92-.058-1.265-.07-1.644-.07-4.849 0-3.204.013-3.583.07-4.849.149-3.227 1.664-4.771 4.919-4.919 1.266-.057 1.645-.069 4.849-.069zm0-2.163c-3.259 0-3.667.014-4.947.072-4.358.2-6.78 2.618-6.98 6.98-.059 1.281-.073 1.689-.073 4.948 0 3.259.014 3.668.072 4.948.2 4.358 2.618 6.78 6.98 6.98 1.281.058 1.689.072 4.948.072 3.259 0 3.668-.014 4.948-.072 4.354-.2 6.782-2.618 6.979-6.98.059-1.28.073-1.689.073-4.948 0-3.259-.014-3.667-.072-4.947-.196-4.354-2.617-6.78-6.979-6.98-1.281-.059-1.69-.073-4.949-.073zm0 5.838c-3.403 0-6.162 2.759-6.162 6.162s2.759 6.163 6.162 6.163 6.162-2.759 6.162-6.163c0-3.403-2.759-6.162-6.162-6.162zm0 10.162c-2.209 0-4-1.79-4-4 0-2.209 1.791-4 4-4s4 1.791 4 4c0 2.21-1.791 4-4 4zm6.406-11.845c-.796 0-1.441.645-1.441 1.44s.645 1.44 1.441 1.44c.795 0 1.439-.645 1.439-1.44s-.644-1.44-1.439-1.44z"/>
                </svg>
                <h3>Instagram Performance</h3>
              </div>
            </div>
            <div className="platform-metrics">
              <div className="metric-row">
                <span>Followers</span>
                <strong>{(analyticsData?.instagram?.current_followers || 0).toLocaleString()}</strong>
              </div>
              <div className="metric-row">
                <span>Engagement Rate</span>
                <strong>{(analyticsData?.instagram?.avg_engagement_rate || 0).toFixed(2)}%</strong>
              </div>
              <div className="metric-row">
                <span>Total Engagement</span>
                <strong>{(analyticsData?.instagram?.total_engagement || 0).toLocaleString()}</strong>
              </div>
              <div className="metric-row">
                <span>Reach</span>
                <strong>{(analyticsData?.instagram?.total_reach || 0).toLocaleString()}</strong>
              </div>
              <div className="metric-row">
                <span>Growth Rate</span>
                <strong>0.0%</strong>
              </div>
            </div>
          </div>

          <div className="platform-card facebook-card">
            <div className="platform-header">
              <div className="platform-title">
                <svg width="20" height="20" fill="currentColor" viewBox="0 0 24 24">
                  <path d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z"/>
                </svg>
                <h3>Facebook Performance</h3>
              </div>
            </div>
            <div className="platform-metrics">
              <div className="metric-row">
                <span>Followers</span>
                <strong>{(analyticsData?.facebook?.follower_trend?.counts?.slice(-1)[0] || 0).toLocaleString()}</strong>
              </div>
              <div className="metric-row">
                <span>Engagement Rate</span>
                <strong>{(analyticsData?.facebook?.avg_engagement_rate || 0).toFixed(2)}%</strong>
              </div>
              <div className="metric-row">
                <span>Total Engagement</span>
                <strong>{(analyticsData?.facebook?.total_engagement || 0).toLocaleString()}</strong>
              </div>
              <div className="metric-row">
                <span>Reach</span>
                <strong>{(analyticsData?.facebook?.total_reach || 0).toLocaleString()}</strong>
              </div>
              <div className="metric-row">
                <span>Growth Rate</span>
                <strong>0.0%</strong>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Best Times Section */}
      <div className="best-times-section">
        <div className="best-times-grid">
          <div className="best-times-card">
            <div className="card-header instagram-header">
              <h3>🕐 Instagram Best Times</h3>
            </div>
            <div className="times-list">
              {instagramBestTimes.length > 0 ? (
                instagramBestTimes.map((time, index) => (
                  <div key={index} className="time-item">
                    <div className="time-rank">{index + 1}</div>
                    <div className="time-details">
                      <div className="time-hour">{formatTime(time.hour)}</div>
                      <div className="time-stats">
                        <span className="engagement">{time.engagement_rate.toFixed(1)}%</span>
                        <span className="posts">({time.post_count} posts)</span>
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <div className="no-data">
                  <p>No optimal times data available</p>
                </div>
              )}
            </div>
          </div>

          <div className="best-times-card">
            <div className="card-header facebook-header">
              <h3>🕐 Facebook Best Times</h3>
            </div>
            <div className="times-list">
              {facebookBestTimes.length > 0 ? (
                facebookBestTimes.map((time, index) => (
                  <div key={index} className="time-item">
                    <div className="time-rank">{index + 1}</div>
                    <div className="time-details">
                      <div className="time-hour">{formatTime(time.hour)}</div>
                      <div className="time-stats">
                        <span className="engagement">{time.engagement_rate.toFixed(1)}%</span>
                        <span className="posts">({time.post_count} posts)</span>
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <div className="no-data">
                  <p>No optimal times data available</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Top Posts Section */}
      <div className="top-posts-section">
        <div className="top-posts-grid">
          <div className="top-posts-card">
            <div className="card-header instagram-header">
              <h3>🎯 Instagram Top Posts</h3>
            </div>
            <div className="posts-list">
              {analyticsData?.instagram?.top_posts?.length > 0 ? (
                analyticsData.instagram.top_posts.slice(0, 3).map((post, index) => (
                  <div key={index} className="post-item">
                    <div className="post-rank">{index + 1}</div>
                    <div className="post-content">
                      <div className="post-text">
                        {post.caption ? post.caption.substring(0, 120) + '...' : 'No caption available'}
                      </div>
                      <div className="post-metrics">
                        <span>❤️ {(post.likes || 0).toLocaleString()}</span>
                        <span>💬 {(post.comments || 0).toLocaleString()}</span>
                        <span>📊 {(post.engagement_rate || 0).toFixed(1)}%</span>
                      </div>
                      {post.permalink_url && (
                        <a href={post.permalink_url} target="_blank" rel="noopener noreferrer" className="view-link">
                          View Post →
                        </a>
                      )}
                    </div>
                  </div>
                ))
              ) : (
                <div className="no-data">
                  <p>No top posts data available</p>
                </div>
              )}
            </div>
          </div>

          <div className="top-posts-card">
            <div className="card-header facebook-header">
              <h3>🎯 Facebook Top Posts</h3>
            </div>
            <div className="posts-list">
              {analyticsData?.facebook?.top_posts?.length > 0 ? (
                analyticsData.facebook.top_posts.slice(0, 3).map((post, index) => (
                  <div key={index} className="post-item">
                    <div className="post-rank">{index + 1}</div>
                    <div className="post-content">
                      <div className="post-text">
                        {post.message ? post.message.substring(0, 120) + '...' : 'No message available'}
                      </div>
                      <div className="post-metrics">
                        <span>👍 {(post.reactions || 0).toLocaleString()}</span>
                        <span>💬 {(post.comments || 0).toLocaleString()}</span>
                        <span>📊 {(post.engagement_rate || 0).toFixed(1)}%</span>
                      </div>
                      {post.permalink_url && (
                        <a href={post.permalink_url} target="_blank" rel="noopener noreferrer" className="view-link">
                          View Post →
                        </a>
                      )}
                    </div>
                  </div>
                ))
              ) : (
                <div className="no-data">
                  <p>No top posts data available</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Growth Summary Section */}
      <div className="growth-summary-section">
        <div className="summary-card">
          <div className="card-header">
            <h3>📈 Growth Comparison Summary</h3>
          </div>
          <div className="summary-grid">
            <div className="summary-item">
              <div className="summary-label">Instagram Growth</div>
              <div className="summary-value">0.0%</div>
            </div>
            <div className="summary-item">
              <div className="summary-label">Facebook Growth</div>
              <div className="summary-value">0.0%</div>
            </div>
            <div className="summary-item">
              <div className="summary-label">Better Platform</div>
              <div className="summary-value">
                {(analyticsData?.instagram?.avg_engagement_rate || 0) >= (analyticsData?.facebook?.avg_engagement_rate || 0) 
                  ? '📸 Instagram' 
                  : '📘 Facebook'}
              </div>
            </div>
            <div className="summary-item">
              <div className="summary-label">Growth Difference</div>
              <div className="summary-value">0.0%</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Analytics;
