  // Chart configurations with proper styling
  const getFollowerTrendData = () => {
    const datasets = [];
    let labels = [];

    // Instagram data
    if (analyticsData?.instagram?.follower_trend?.dates?.length) {
      const { dates, counts } = analyticsData.instagram.follower_trend;
      labels = dates.map(date => new Date(date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' }));
      datasets.push({
        label: 'Instagram Followers',
        data: counts,
        borderColor: '#e4405f',
        backgroundColor: 'rgba(228, 64, 95, 0.1)',
        tension: 0.4,
        fill: false,
        pointBackgroundColor: '#e4405f',
        pointBorderColor: '#ffffff',
        pointBorderWidth: 2,
        pointRadius: 4
      });
    }

    // Facebook data
    if (analyticsData?.facebook?.follower_trend?.dates?.length) {
      const { dates, counts } = analyticsData.facebook.follower_trend;
      if (labels.length === 0) {
        labels = dates.map(date => new Date(date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' }));
