import { Line, Bar, Pie, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';
import apiClient from '../api';
import '../styles/analytics.css';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Analytics = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [selectedPeriod, setSelectedPeriod] = useState('30d');
  const [analyticsData, setAnalyticsData] = useState({
    instagram: null,
    facebook: null,
    engagement: null,
    reach: null
  });

  // Fetch all analytics data
  const fetchAnalytics = async (period = '30d') => {
    setLoading(true);
    setError(null);

    try {
      console.log(`Fetching analytics for period: ${period}`);

      // Fetch all analytics data in parallel
      const [
        generalResponse,
        engagementResponse,
        reachResponse
      ] = await Promise.all([
        apiClient.getAnalytics('all', period),
        apiClient.getEngagementStats('all'),
        apiClient.getReachStats('all')
      ]);

      console.log('General analytics:', generalResponse);
      console.log('Engagement analytics:', engagementResponse);
      console.log('Reach analytics:', reachResponse);

      setAnalyticsData({
        instagram: generalResponse.data?.instagram || {},
        facebook: generalResponse.data?.facebook || {},
        engagement: engagementResponse.data || {},
        reach: reachResponse.data || {}
      });

      setLastUpdated(new Date());
      setError(null);
    } catch (err) {
      console.error('Error fetching analytics:', err);
      setError('Failed to load analytics data. Please check your API configuration and try again.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAnalytics(selectedPeriod);
  }, [selectedPeriod]);

  // Auto-refresh data every 5 minutes
  useEffect(() => {
    const interval = setInterval(() => {
      fetchAnalytics(selectedPeriod);
    }, 5 * 60 * 1000); // 5 minutes

    return () => clearInterval(interval);
  }, [selectedPeriod]);

  const handlePeriodChange = (period) => {
    setSelectedPeriod(period);
  };

  const handleRefresh = () => {
    fetchAnalytics(selectedPeriod);
  };

  if (loading) {
    return (
      <div className="analytics-dashboard">
        <div className="flex justify-center items-center h-64">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto mb-4"></div>
            <div className="text-lg font-medium text-gray-600">Loading analytics data...</div>
            <div className="text-sm text-gray-500 mt-2">This may take a few moments</div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="analytics-dashboard">
        <div className="bg-red-50 border border-red-200 text-red-700 px-6 py-4 rounded-lg" role="alert">
          <div className="flex items-center">
            <svg className="w-5 h-5 mr-2" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
            </svg>
            <div>
              <strong className="font-bold">Error!</strong>
              <span className="block sm:inline ml-1">{error}</span>
            </div>
          </div>
          <button 
            onClick={handleRefresh}
            className="mt-3 bg-red-100 hover:bg-red-200 text-red-800 px-4 py-2 rounded-md text-sm font-medium transition-colors"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  // Process follower trend data
  const instagramFollowerTrend = analyticsData.instagram?.follower_trend || { dates: [], counts: [] };
  const facebookFollowerTrend = analyticsData.facebook?.follower_trend || { dates: [], counts: [] };

  // Create follower trend chart data
  const followerTrendData = {
    labels: instagramFollowerTrend.dates?.map(date => 
      new Date(date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })
    ) || [],
    datasets: [
      {
        label: 'Instagram Followers',
        data: instagramFollowerTrend.counts || [],
        borderColor: '#E4405F',
        backgroundColor: 'rgba(228, 64, 95, 0.1)',
        tension: 0.3,
        fill: true
      },
      {
        label: 'Facebook Followers',
        data: facebookFollowerTrend.counts || [],
        borderColor: '#1877F2',
        backgroundColor: 'rgba(24, 119, 242, 0.1)',
        tension: 0.3,
        fill: true
      }
    ],
  };

  // Process engagement data
  const instagramEngagement = analyticsData.engagement?.instagram || {};
  const facebookEngagement = analyticsData.engagement?.facebook || {};

  // Create engagement comparison chart
  const engagementComparisonData = {
    labels: ['Likes', 'Comments', 'Shares', 'Saves'],
    datasets: [
      {
        label: 'Instagram',
        data: [
          instagramEngagement.avg_likes || 0,
          instagramEngagement.avg_comments || 0,
          instagramEngagement.avg_shares || 0,
          instagramEngagement.avg_saves || 0
        ],
        backgroundColor: 'rgba(228, 64, 95, 0.8)',
      },
      {
        label: 'Facebook',
        data: [
          facebookEngagement.avg_likes || 0,
          facebookEngagement.avg_comments || 0,
          facebookEngagement.avg_shares || 0,
          facebookEngagement.avg_reactions || 0
        ],
        backgroundColor: 'rgba(24, 119, 242, 0.8)',
      }
    ],
  };

  // Best times to post data
  const instagramBestTimes = instagramEngagement.best_times || {};
  const facebookBestTimes = facebookEngagement.best_times || {};

  const bestTimesData = {
    labels: Array.from({length: 24}, (_, i) => `${i}:00`),
    datasets: [
      {
        label: 'Instagram Engagement Score',
        data: Array.from({length: 24}, (_, i) => instagramBestTimes[i] || 0),
        borderColor: '#E4405F',
        backgroundColor: 'rgba(228, 64, 95, 0.1)',
        tension: 0.3
      },
      {
        label: 'Facebook Engagement Score',
        data: Array.from({length: 24}, (_, i) => facebookBestTimes[i] || 0),
        borderColor: '#1877F2',
        backgroundColor: 'rgba(24, 119, 242, 0.1)',
        tension: 0.3
      }
    ],
  };

