  const platformDistributionData = {
    labels: ['Instagram', 'Facebook'],
    datasets: [
      {
        data: [
          analyticsData.instagram?.total_engagement || 0,
          analyticsData.facebook?.total_engagement || 0,
        ],
        backgroundColor: ['#E4405F', '#1877F2'],
        borderWidth: 0
      },
    ],
  };

  // Extract key metrics with fallbacks
  const metrics = {
    instagram: {
      followers: analyticsData.instagram?.total_followers_gained || 0,
      engagementRate: analyticsData.instagram?.avg_engagement_rate || 0,
      totalEngagement: analyticsData.instagram?.total_engagement || 0,
      reach: analyticsData.reach?.instagram?.total_reach || 0,
      impressions: analyticsData.reach?.instagram?.total_impressions || 0
    },
    facebook: {
      followers: analyticsData.facebook?.total_followers_gained || 0,
      engagementRate: analyticsData.facebook?.engagement_rate || 0,
      totalEngagement: analyticsData.facebook?.total_engagement || 0,
      reach: analyticsData.reach?.facebook?.total_reach || 0,
      impressions: analyticsData.reach?.facebook?.total_impressions || 0
    }
  };

