import React, { useState, useEffect } from 'react';
import { ClockIcon, CheckCircleIcon, XCircleIcon } from '@heroicons/react/24/outline';
import apiClient from '../api';
import toast from 'react-hot-toast';
import { formatToIST } from '../utils/dateFormatter';

const ScheduledPosts = () => {
  const [posts, setPosts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [filterPlatform, setFilterPlatform] = useState('All');
  const [filterStatus, setFilterStatus] = useState('All');

  useEffect(() => {
    loadPosts();
  }, []);

  const loadPosts = async () => {
    setIsLoading(true);
    try {
      console.log('Fetching scheduled posts...');
      const response = await apiClient.getPosts();
      console.log('Posts response:', response);

      // Handle different response formats
      if (Array.isArray(response)) {
        setPosts(response);
      } else if (response && Array.isArray(response.posts)) {
        setPosts(response.posts);
      } else {
        setPosts([]);
        console.warn('Unexpected response format:', response);
      }
    } catch (error) {
      console.error('Error loading posts:', error);
      toast.error('Failed to load scheduled posts');
      setPosts([]);
    } finally {
      setIsLoading(false);
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'scheduled':
        return <ClockIcon className="h-5 w-5 text-blue-500" />;
      case 'posted':
        return <CheckCircleIcon className="h-5 w-5 text-green-500" />;
      case 'failed':
        return <XCircleIcon className="h-5 w-5 text-red-500" />;
      default:
        return <ClockIcon className="h-5 w-5 text-gray-500" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'scheduled':
        return 'bg-blue-100 text-blue-800';
      case 'posted':
        return 'bg-green-100 text-green-800';
      case 'failed':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const filteredPosts = posts.filter(post => {
    // Handle possible null or undefined values
    const postPlatform = post?.platforms || post?.platform || 'Instagram';
    const postStatus = post?.status || 'scheduled';

    const platformMatch = filterPlatform === 'All' || postPlatform.includes(filterPlatform);
    const statusMatch = filterStatus === 'All' || postStatus === filterStatus;
    return platformMatch && statusMatch;
  });

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold text-gray-900">Scheduled Posts</h1>
        <button
          onClick={loadPosts}
          className="btn-secondary"
          disabled={isLoading}
        >
          {isLoading ? 'Refreshing...' : 'Refresh'}
        </button>
      </div>

      {/* Filters */}
      <div className="card">
        <div className="flex space-x-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Platform
            </label>
            <select
              value={filterPlatform}
              onChange={(e) => setFilterPlatform(e.target.value)}
              className="input-field"
            >
              <option value="All">All Platforms</option>
              <option value="Instagram">Instagram</option>
              <option value="Facebook">Facebook</option>
              <option value="Both">Both</option>
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Status
            </label>
            <select
              value={filterStatus}
              onChange={(e) => setFilterStatus(e.target.value)}
              className="input-field"
            >
              <option value="All">All Status</option>
              <option value="scheduled">Scheduled</option>
              <option value="posting_in_progress">In Progress</option>
              <option value="posted">Posted</option>
              <option value="failed">Failed</option>
            </select>
          </div>
        </div>
      </div>

      {/* Posts List */}
      {isLoading ? (
        <div className="flex justify-center items-center h-64">
          <div className="text-lg font-medium text-gray-600">Loading posts...</div>
        </div>
      ) : filteredPosts.length === 0 ? (
        <div className="card text-center py-12">
          <ClockIcon className="h-12 w-12 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">No scheduled posts</h3>
          <p className="text-gray-600 mb-4">You haven't scheduled any posts yet.</p>
          <a href="/scheduler" className="btn-primary">
            Schedule Your First Post
          </a>
        </div>
      ) : (
        <div className="space-y-4">
          {filteredPosts.map((post) => (
            <div key={post.id} className="card">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <div className="flex items-center space-x-3 mb-2">
                    {getStatusIcon(post.status)}
                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(post.status)}`}>
                      {post.status.replace('_', ' ').toUpperCase()}
                    </span>
                    <span className="text-sm text-gray-500">
                      {post.platforms || post.platform || 'Instagram'}
                    </span>
                    <span className="text-sm text-gray-500">
                      {post.media_type}
                    </span>
                  </div>

                  <p className="text-gray-900 mb-2 line-clamp-3">
                    {post.caption}
                  </p>

                  <div className="flex items-center space-x-4 text-sm text-gray-500">
                    <span>
                      Scheduled: {formatToIST(post.scheduled_time, 'MMM DD, YYYY, hh:mm A')} IST
                    </span>
                    {post.posting_attempt_at && (
                      <span>
                        Attempted: {formatToIST(post.posting_attempt_at, 'MMM DD, YYYY, hh:mm A')} IST
                      </span>
                    )}
                  </div>

                  {post.error_message && (
                    <div className="mt-2 p-2 bg-red-50 border border-red-200 rounded text-sm text-red-700">
                      Error: {post.error_message}
                    </div>
                  )}
                </div>

                {post.media_urls && post.media_urls.length > 0 && (
                  <div className="ml-4">
                    <img
                      src={post.media_urls[0]}
                      alt="Post preview"
                      className="w-16 h-16 object-cover rounded-lg"
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = 'https://via.placeholder.com/150?text=Media+Not+Available';
                      }}
                    />
                    {post.media_urls.length > 1 && (
                      <span className="text-xs text-gray-500 mt-1 block">
                        +{post.media_urls.length - 1} more
                      </span>
                    )}
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ScheduledPosts;
