import React, { useEffect, useState } from 'react';
import { Line, Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';
import '../styles/analytics.css';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Analytics = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [selectedPeriod, setSelectedPeriod] = useState('30d');
  const [analyticsData, setAnalyticsData] = useState({
    summary: null,
    growth: null,
    instagram: null,
    facebook: null,
    engagement: null,
    reach: null
  });

  // Fetch all analytics data
  const fetchAnalytics = async (period = '30d') => {
    setLoading(true);
    setError(null);

    try {
      // Add cache-busting parameter to force fresh data
      const timestamp = Date.now();
      const response = await fetch(`/api/analytics/summary?period=${period}&_t=${timestamp}`, {
        headers: {
          'Cache-Control': 'no-cache',
          'Pragma': 'no-cache'
        }
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      console.log('ðŸ” Fresh Analytics Data Received:', data);

      // Force state update
      setAnalyticsData(data);
      setLastUpdated(new Date());

      // Log metrics calculation
      const calculatedMetrics = {
        totalFollowers: data.summary?.overview?.total_followers || 0,
        instagramFollowers: data.instagram?.current_followers || 0,
        totalEngagement: data.summary?.overview?.total_engagement || 0,
        totalReach: data.summary?.overview?.total_reach || 0
      };
      console.log('ðŸ“Š Calculated Metrics:', calculatedMetrics);

    } catch (err) {
      console.error('Analytics fetch error:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAnalytics(selectedPeriod);
  }, [selectedPeriod]);

  const handlePeriodChange = (period) => {
    setSelectedPeriod(period);
  };

  const handleRefresh = () => {
    fetchAnalytics(selectedPeriod);
  };

  if (loading) {
    return (
      <div className="analytics-dashboard">
        <div className="analytics-loading fade-in">
          <div className="loading-spinner"></div>
          <div className="loading-text">Loading Analytics Dashboard</div>
          <div className="loading-subtext">Fetching your social media insights...</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="analytics-dashboard">
        <div className="analytics-error fade-in">
          <div className="flex items-center">
            <svg className="w-5 h-5 mr-2" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
            </svg>
            <div>
              <strong className="font-bold">Analytics Error!</strong>
              <span className="block sm:inline ml-1">{error}</span>
            </div>
          </div>
          <button 
            onClick={handleRefresh}
            className="mt-3 bg-red-100 hover:bg-red-200 text-red-800 px-4 py-2 rounded-md text-sm font-medium transition-colors"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  // Process follower trend data - use the actual data from backend
  const instagramData = analyticsData.instagram || {};
  const facebookData = analyticsData.facebook || {};

  // Get follower trend data from the correct structure
  const instagramTrend = instagramData.follower_trend || { dates: [], counts: [] };
  const facebookTrend = facebookData.follower_trend || { dates: [], counts: [] };

  // Create follower trend chart data
  const followerTrendData = {
    labels: instagramTrend.dates?.map(date => 
      new Date(date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })
    ) || facebookTrend.dates?.map(date => 
      new Date(date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })
    ) || [],
    datasets: [
      {
        label: 'Instagram Followers',
        data: instagramTrend.counts || [],
        borderColor: '#E4405F',
        backgroundColor: 'rgba(228, 64, 95, 0.1)',
        tension: 0.4,
        fill: true,
        pointBackgroundColor: '#E4405F',
        pointBorderColor: '#fff',
        pointBorderWidth: 2,
        pointRadius: 4
      },
      {
        label: 'Facebook Followers',
        data: facebookTrend.counts || [],
        borderColor: '#1877F2',
        backgroundColor: 'rgba(24, 119, 242, 0.1)',
        tension: 0.4,
        fill: true,
        pointBackgroundColor: '#1877F2',
        pointBorderColor: '#fff',
        pointBorderWidth: 2,
        pointRadius: 4
      }
    ],
  };

  // Process engagement data
  const instagramEngagement = analyticsData.engagement?.instagram || {};
  const facebookEngagement = analyticsData.engagement?.facebook || {};

  // Create engagement comparison chart
  const engagementComparisonData = {
    labels: ['Likes', 'Comments', 'Shares', 'Saves/Reactions'],
    datasets: [
      {
        label: 'Instagram',
        data: [
          instagramEngagement.avg_likes || 0,
          instagramEngagement.avg_comments || 0,
          instagramEngagement.avg_shares || 0,
          instagramEngagement.avg_saves || 0
        ],
        backgroundColor: 'rgba(228, 64, 95, 0.8)',
        borderRadius: 8,
        borderSkipped: false,
      },
      {
        label: 'Facebook',
        data: [
          facebookEngagement.avg_likes || 0,
          facebookEngagement.avg_comments || 0,
          facebookEngagement.avg_shares || 0,
          facebookEngagement.avg_reactions || 0
        ],
        backgroundColor: 'rgba(24, 119, 242, 0.8)',
        borderRadius: 8,
        borderSkipped: false,
      }
    ],
  };

  // Best times to post data
  const instagramBestTimes = analyticsData.engagement?.instagram?.best_times || {};
  const facebookBestTimes = analyticsData.engagement?.facebook?.best_times || {};

  const bestTimesData = {
    labels: Array.from({length: 24}, (_, i) => `${i}:00`),
    datasets: [
      {
        label: 'Instagram Engagement Rate (%)',
        data: Array.from({length: 24}, (_, i) => instagramBestTimes[i]?.engagement_rate || 0),
        borderColor: '#E4405F',
        backgroundColor: 'rgba(228, 64, 95, 0.1)',
        tension: 0.4,
        fill: true,
        pointBackgroundColor: '#E4405F',
        pointBorderColor: '#fff',
        pointBorderWidth: 2,
        pointRadius: 3
      },
      {
        label: 'Facebook Engagement Rate (%)',
        data: Array.from({length: 24}, (_, i) => facebookBestTimes[i]?.engagement_rate || 0),
        borderColor: '#1877F2',
        backgroundColor: 'rgba(24, 119, 242, 0.1)',
        tension: 0.4,
        fill: true,
        pointBackgroundColor: '#1877F2',
        pointBorderColor: '#fff',
        pointBorderWidth: 2,
        pointRadius: 3
      }
    ],
  };

  // Platform distribution data
  const platformDistributionData = {
    labels: ['Instagram', 'Facebook'],
    datasets: [
      {
        data: [
          analyticsData.instagram?.total_engagement || 1,
          analyticsData.facebook?.total_engagement || 0,
        ],
        backgroundColor: ['#E4405F', '#1877F2'],
        borderWidth: 0,
        hoverOffset: 10
      },
    ],
  };

  // Extract key metrics with correct data paths
  const metrics = {
    overview: {
      totalFollowers: analyticsData.summary?.overview?.total_followers || 0,
      totalEngagement: analyticsData.summary?.overview?.total_engagement || 0,
      totalReach: analyticsData.summary?.overview?.total_reach || 0,
      avgEngagementRate: analyticsData.summary?.overview?.avg_engagement_rate || 0,
      followerGrowth: analyticsData.summary?.overview?.follower_growth || 0,
      totalPosts: analyticsData.summary?.overview?.total_posts || 0
    },
    instagram: {
      followers: analyticsData.instagram?.current_followers || 
                (analyticsData.instagram?.follower_trend?.counts?.length > 0 
                  ? analyticsData.instagram.follower_trend.counts[analyticsData.instagram.follower_trend.counts.length - 1]
                  : analyticsData.summary?.platforms?.instagram?.followers || 0),
      engagementRate: analyticsData.instagram?.avg_engagement_rate || 
                     analyticsData.summary?.platforms?.instagram?.engagement_rate || 0,
      totalEngagement: analyticsData.instagram?.total_engagement || 0,
      reach: analyticsData.instagram?.total_reach || 
             analyticsData.reach?.instagram?.total_reach || 0,
      posts: analyticsData.instagram?.total_posts || 0,
      growthRate: analyticsData.growth?.instagram?.growth_rate || 0
    },
    facebook: {
      followers: analyticsData.facebook?.follower_trend?.counts?.length > 0 
        ? analyticsData.facebook.follower_trend.counts[analyticsData.facebook.follower_trend.counts.length - 1]
        : analyticsData.summary?.platforms?.facebook?.followers || 0,
      engagementRate: analyticsData.facebook?.avg_engagement_rate || 
                     analyticsData.summary?.platforms?.facebook?.engagement_rate || 0,
      totalEngagement: analyticsData.facebook?.total_engagement || 0,
      reach: analyticsData.facebook?.total_reach || 
             analyticsData.reach?.facebook?.total_reach || 0,
      posts: analyticsData.facebook?.total_posts || 0,
      growthRate: analyticsData.growth?.facebook?.growth_rate || 0
    }
  };

  return (
    <div className="analytics-dashboard fade-in">
      {/* Header Section */}
      <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center gap-6 mb-8">
        <div>
          <h1>ðŸ“Š Analytics Dashboard</h1>
          {lastUpdated && (
            <p className="mt-2">
              Last updated: {lastUpdated.toLocaleString()} â€¢ Analytics will refresh when manually triggered
            </p>
          )}
        </div>

        <div className="analytics-controls">
          <select 
            value={selectedPeriod} 
            onChange={(e) => handlePeriodChange(e.target.value)}
            className="period-selector"
          >
            <option value="7d">Last 7 days</option>
            <option value="30d">Last 30 days</option>
            <option value="90d">Last 90 days</option>
          </select>

          <button onClick={handleRefresh} className="refresh-button">
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
            </svg>
            Refresh
          </button>
        </div>
      </div>

      {/* Key Metrics Overview */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8 slide-up">
        <div className="metric-card hover-lift">
          <div className="metric-header">
            <div className="metric-content">
              <h3>Total Followers</h3>
              <p className="metric-value">{metrics.overview.totalFollowers.toLocaleString()}</p>
            </div>
            <div className="metric-icon primary">
              <svg className="w-6 h-6 text-white" fill="currentColor" viewBox="0 0 24 24">
                <path d="M16 4c0-1.11.89-2 2-2s2 .89 2 2-.89 2-2 2-2-.89-2-2zm4 18v-6h2.5l-2.54-7.63A1.5 1.5 0 0 0 18.5 7h-5c-.8 0-1.5.7-1.5 1.5v6c0 .8.7 1.5 1.5 1.5H16v6h4zM12.5 11.5c.8 0 1.5-.7 1.5-1.5s-.7-1.5-1.5-1.5S11 9.2 11 10s.7 1.5 1.5 1.5zM7 22v-6H4.5l2.54-7.63A1.5 1.5 0 0 1 8.5 7h5c.8 0 1.5.7 1.5 1.5v6c0 .8-.7 1.5-1.5 1.5H11v6H7z"/>
              </svg>
            </div>
          </div>
          <div className="metric-footer">
            <div className="metric-stat">
              <span className="metric-label">Growth:</span>
              <span className={`metric-value-small ${metrics.overview.followerGrowth >= 0 ? 'text-success' : 'text-error'}`}>
                {metrics.overview.followerGrowth > 0 ? '+' : ''}{metrics.overview.followerGrowth}
              </span>
            </div>
            <div className="metric-stat">
              <span className="metric-label">Posts:</span>
              <span className="metric-value-small">{metrics.overview.totalPosts}</span>
            </div>
          </div>
        </div>

        <div className="metric-card hover-lift">
          <div className="metric-header">
            <div className="metric-content">
              <h3>Instagram</h3>
              <p className="metric-value">{metrics.instagram.followers.toLocaleString()}</p>
            </div>
            <div className="metric-icon instagram">
              <svg className="w-6 h-6 text-white" fill="currentColor" viewBox="0 0 24 24">
                <path d="M12 2.163c3.204 0 3.584.012 4.85.07 3.252.148 4.771 1.691 4.919 4.919.058 1.265.069 1.645.069 4.849 0 3.205-.012 3.584-.069 4.849-.149 3.225-1.664 4.771-4.919 4.919-1.266.058-1.644.07-4.85.07-3.204 0-3.584-.012-4.849-.07-3.26-.149-4.771-1.699-4.919-4.92-.058-1.265-.07-1.644-.07-4.849 0-3.204.013-3.583.07-4.849.149-3.227 1.664-4.771 4.919-4.919 1.266-.057 1.645-.069 4.849-.069zm0-2.163c-3.259 0-3.667.014-4.947.072-4.358.2-6.78 2.618-6.98 6.98-.059 1.281-.073 1.689-.073 4.948 0 3.259.014 3.668.072 4.948.2 4.358 2.618 6.78 6.98 6.98 1.281.058 1.689.072 4.948.072 3.259 0 3.668-.014 4.948-.072 4.354-.2 6.782-2.618 6.979-6.98.059-1.28.073-1.689.073-4.948 0-3.259-.014-3.667-.072-4.947-.196-4.354-2.617-6.78-6.979-6.98-1.281-.059-1.69-.073-4.949-.073zm0 5.838c-3.403 0-6.162 2.759-6.162 6.162s2.759 6.163 6.162 6.163 6.162-2.759 6.162-6.163c0-3.403-2.759-6.162-6.162-6.162zm0 10.162c-2.209 0-4-1.79-4-4 0-2.209 1.791-4 4-4s4 1.791 4 4c0 2.21-1.791 4-4 4zm6.406-11.845c-.796 0-1.441.645-1.441 1.44s.645 1.44 1.441 1.44c.795 0 1.439-.645 1.439-1.44s-.644-1.44-1.439-1.44z"/>
              </svg>
            </div>
          </div>
          <div className="metric-footer">
            <div className="metric-stat">
              <span className="metric-label">Engagement:</span>
              <span className="metric-value-small">{metrics.instagram.engagementRate.toFixed(2)}%</span>
            </div>
            <div className="metric-stat">
              <span className="metric-label">Growth:</span>
              <span className={`metric-value-small ${metrics.instagram.growthRate >= 0 ? 'text-success' : 'text-error'}`}>
                {metrics.instagram.growthRate > 0 ? '+' : ''}{metrics.instagram.growthRate.toFixed(1)}%
              </span>
            </div>
          </div>
        </div>

        <div className="metric-card hover-lift">
          <div className="metric-header">
            <div className="metric-content">
              <h3>Facebook</h3>
              <p className="metric-value">{metrics.facebook.followers.toLocaleString()}</p>
            </div>
            <div className="metric-icon facebook">
              <svg className="w-6 h-6 text-white" fill="currentColor" viewBox="0 0 24 24">
                <path d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z"/>
              </svg>
            </div>
          </div>
          <div className="metric-footer">
            <div className="metric-stat">
              <span className="metric-label">Engagement:</span>
              <span className="metric-value-small">{metrics.facebook.engagementRate.toFixed(2)}%</span>
            </div>
            <div className="metric-stat">
              <span className="metric-label">Growth:</span>
              <span className={`metric-value-small ${metrics.facebook.growthRate >= 0 ? 'text-success' : 'text-error'}`}>
                {metrics.facebook.growthRate > 0 ? '+' : ''}{metrics.facebook.growthRate.toFixed(1)}%
              </span>
            </div>
          </div>
        </div>

        <div className="metric-card hover-lift">
          <div className="metric-header">
            <div className="metric-content">
              <h3>Total Reach</h3>
              <p className="metric-value">{metrics.overview.totalReach.toLocaleString()}</p>
            </div>
            <div className="metric-icon success">
              <svg className="w-6 h-6 text-white" fill="currentColor" viewBox="0 0 24 24">
                <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z"/>
              </svg>
            </div>
          </div>
          <div className="metric-footer">
            <div className="metric-stat">
              <span className="metric-label">Engagements:</span>
              <span className="metric-value-small">{metrics.overview.totalEngagement.toLocaleString()}</span>
            </div>
            <div className="metric-stat">
              <span className="metric-label">Avg Rate:</span>
              <span className="metric-value-small">{metrics.overview.avgEngagementRate.toFixed(2)}%</span>
            </div>
          </div>
        </div>
      </div>

      {/* Charts Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8 slide-up" style={{ animationDelay: '0.2s' }}>
        {/* Follower Growth Trend */}
        <div className="chart-container hover-lift">
          <h3 className="chart-title">ðŸ“ˆ Follower Growth Trend</h3>
          <div className="chart-content">
            <Line 
              data={followerTrendData} 
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  legend: {
                    position: 'top',
                    labels: {
                      padding: 20,
                      usePointStyle: true,
                      font: { weight: 'bold' }
                    }
                  },
                  tooltip: {
                    mode: 'index',
                    intersect: false,
                    backgroundColor: 'rgba(0, 0, 0, 0.8)',
                    titleColor: '#fff',
                    bodyColor: '#fff',
                    cornerRadius: 8,
                    padding: 12
                  }
                },
                scales: {
                  y: {
                    beginAtZero: false,
                    title: {
                      display: true,
                      text: 'Followers',
                      font: { weight: 'bold', size: 14 }
                    },
                    grid: {
                      color: 'rgba(0, 0, 0, 0.05)'
                    }
                  },
                  x: {
                    title: {
                      display: true,
                      text: 'Date',
                      font: { weight: 'bold', size: 14 }
                    },
                    grid: {
                      color: 'rgba(0, 0, 0, 0.05)'
                    }
                  }
                },
                interaction: {
                  mode: 'nearest',
                  axis: 'x',
                  intersect: false
                }
              }}
            />
          </div>
        </div>

        {/* Engagement Comparison */}
        <div className="chart-container hover-lift">
          <h3 className="chart-title">ðŸ’¬ Engagement Comparison</h3>
          <div className="chart-content">
            <Bar 
              data={engagementComparisonData} 
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  legend: {
                    position: 'top',
                    labels: {
                      padding: 20,
                      usePointStyle: true,
                      font: { weight: 'bold' }
                    }
                  },
                  tooltip: {
                    backgroundColor: 'rgba(0, 0, 0, 0.8)',
                    titleColor: '#fff',
                    bodyColor: '#fff',
                    cornerRadius: 8,
                    padding: 12
                  }
                },
                scales: {
                  y: {
                    beginAtZero: true,
                    title: {
                      display: true,
                      text: 'Average Count',
                      font: { weight: 'bold', size: 14 }
                    },
                    grid: {
                      color: 'rgba(0, 0, 0, 0.05)'
                    }
                  },
                  x: {
                    grid: {
                      display: false
                    }
                  }
                }
              }}
            />
          </div>
        </div>

        {/* Best Times to Post */}
        <div className="chart-container hover-lift">
          <h3 className="chart-title">â° Best Times to Post</h3>
          <div className="chart-content">
            <Line 
              data={bestTimesData} 
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  legend: {
                    position: 'top',
                    labels: {
                      padding: 20,
                      usePointStyle: true,
                      font: { weight: 'bold' }
                    }
                  },
                  tooltip: {
                    backgroundColor: 'rgba(0, 0, 0, 0.8)',
                    titleColor: '#fff',
                    bodyColor: '#fff',
                    cornerRadius: 8,
                    padding: 12
                  }
                },
                scales: {
                  y: {
                    beginAtZero: true,
                    title: {
                      display: true,
                      text: 'Engagement Score',
                      font: { weight: 'bold', size: 14 }
                    },
                    grid: {
                      color: 'rgba(0, 0, 0, 0.05)'
                    }
                  },
                  x: {
                    title: {
                      display: true,
                      text: 'Hour of Day',
                      font: { weight: 'bold', size: 14 }
                    },
                    grid: {
                      color: 'rgba(0, 0, 0, 0.05)'
                    }
                  }
                }
              }}
            />
          </div>
        </div>

        {/* Platform Distribution */}
        <div className="chart-container hover-lift">
          <h3 className="chart-title">ðŸ† Engagement Distribution</h3>
          <div className="chart-content">
            <div className="flex justify-center items-center h-full">
              <Doughnut 
                data={platformDistributionData} 
                options={{
                  responsive: true,
                  maintainAspectRatio: false,
                  plugins: {
                    legend: {
                      position: 'bottom',
                      labels: {
                        padding: 20,
                        usePointStyle: true,
                        font: { weight: 'bold', size: 14 }
                      }
                    },
                    tooltip: {
                      backgroundColor: 'rgba(0, 0, 0, 0.8)',
                      titleColor: '#fff',
                      bodyColor: '#fff',
                      cornerRadius: 8,
                      padding: 12,
                      callbacks: {
                        label: function(context) {
                          const label = context.label || '';
                          const value = context.parsed || 0;
                          const total = context.dataset.data.reduce((a, b) => a + b, 0);
                          const percentage = total > 0 ? ((value / total) * 100).toFixed(1) : 0;
                          return `${label}: ${value.toLocaleString()} (${percentage}%)`;
                        }
                      }
                    }
                  }
                }}
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Analytics;
