import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { 
  CalendarDaysIcon, 
  ChartBarIcon, 
  ClockIcon, 
  PhotoIcon,
  UsersIcon,
  EyeIcon,
  HeartIcon
} from '@heroicons/react/24/outline';
import apiClient from '../api';
import { formatLocalDateTime } from '../utils/dateFormatter';
import '../styles/dashboard.css';

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState({
    posts: [],
    analytics: {
      summary: null,
      instagram: null,
      facebook: null
    },
    isLoading: true,
    error: null,
    lastUpdated: null
  });

  const fetchDashboardData = async () => {
    try {
      console.log('Fetching dashboard data...');

      // Fetch data in parallel for better performance
      const [
        postsResponse,
        summaryResponse,
        analyticsResponse
      ] = await Promise.all([
        apiClient.getPosts(),
        apiClient.getAnalyticsSummary('30d').catch(err => {
          console.warn('Analytics summary failed:', err);
          return { data: null };
        }),
        apiClient.getAnalytics('all', '30d').catch(err => {
          console.warn('Analytics failed:', err);
          return { data: { instagram: null, facebook: null } };
        })
      ]);

      console.log('Dashboard data fetched:', {
        posts: postsResponse,
        summary: summaryResponse,
        analytics: analyticsResponse
      });

      setDashboardData({
        posts: postsResponse.posts || [],
        analytics: {
          summary: summaryResponse.data || null,
          instagram: analyticsResponse.data?.instagram || null,
          facebook: analyticsResponse.data?.facebook || null
        },
        isLoading: false,
        error: null,
        lastUpdated: new Date()
      });
    } catch (error) {
      console.error('Error loading dashboard data:', error);
      setDashboardData(prev => ({ 
        ...prev, 
        isLoading: false, 
        error: error.message || 'Failed to load dashboard data'
      }));
    }
  };

  useEffect(() => {
    fetchDashboardData();

    // Auto-refresh every 5 minutes
    const interval = setInterval(fetchDashboardData, 5 * 60 * 1000);
    return () => clearInterval(interval);
  }, []);

  // Process metrics with safe fallbacks
  const processMetrics = () => {
    const { analytics, posts } = dashboardData;

    // Posts metrics
    const scheduledPosts = posts.filter(post => post.status === 'scheduled').length;
    const publishedPosts = posts.filter(post => post.status === 'posted').length;
    const totalPosts = posts.length;

    // Instagram metrics
    const instagramMetrics = {
      followers: analytics.summary?.platform_breakdown?.instagram?.followers || 
                analytics.instagram?.total_followers_gained || 0,
      engagement: analytics.summary?.platform_breakdown?.instagram?.engagement || 
                 analytics.instagram?.total_engagement || 0,
      engagementRate: analytics.summary?.platform_breakdown?.instagram?.engagement_rate || 
                     analytics.instagram?.avg_engagement_rate || 0,
      reach: analytics.summary?.platform_breakdown?.instagram?.reach || 
             analytics.instagram?.total_reach || 0,
      posts: analytics.summary?.platform_breakdown?.instagram?.posts || 
             analytics.instagram?.total_posts || 0
    };

    // Facebook metrics
    const facebookMetrics = {
      followers: analytics.summary?.platform_breakdown?.facebook?.followers || 
                analytics.facebook?.total_followers_gained || 0,
      engagement: analytics.summary?.platform_breakdown?.facebook?.engagement || 
                 analytics.facebook?.total_engagement || 0,
      engagementRate: analytics.summary?.platform_breakdown?.facebook?.engagement_rate || 
                     analytics.facebook?.engagement_rate || 0,
      reach: analytics.summary?.platform_breakdown?.facebook?.reach || 
             analytics.facebook?.total_reach || 0,
      posts: analytics.summary?.platform_breakdown?.facebook?.posts || 
             analytics.facebook?.total_posts || 0
    };

    // Overview metrics
    const overviewMetrics = {
      totalFollowers: analytics.summary?.overview?.total_followers || 
                     (instagramMetrics.followers + facebookMetrics.followers),
      totalEngagement: analytics.summary?.overview?.total_engagement || 
                      (instagramMetrics.engagement + facebookMetrics.engagement),
      totalReach: analytics.summary?.overview?.total_reach || 
                 (instagramMetrics.reach + facebookMetrics.reach),
      avgEngagementRate: analytics.summary?.overview?.avg_engagement_rate || 
                        ((instagramMetrics.engagementRate + facebookMetrics.engagementRate) / 2)
    };

    return {
      posts: { scheduledPosts, publishedPosts, totalPosts },
      instagram: instagramMetrics,
      facebook: facebookMetrics,
      overview: overviewMetrics
    };
  };

  const metrics = processMetrics();

  // Get recent posts for display
  const recentPosts = dashboardData.posts
    .sort((a, b) => new Date(b.scheduled_time || b.created_at) - new Date(a.scheduled_time || a.created_at))
    .slice(0, 5);

  // Loading state
  if (dashboardData.isLoading) {
    return (
      <div className="dashboard">
        <div className="loading-container fade-in">
          <div className="loading-spinner"></div>
          <div className="loading-text">Loading Dashboard</div>
          <div className="loading-subtext">Fetching your social media insights...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard fade-in">
      {/* Header */}
      <div className="dashboard-header">
        <h1 className="dashboard-title">
          📊 Social Media Dashboard
        </h1>
        <p className="dashboard-subtitle">
          Welcome back! Here's what's happening with your social media presence.
          {dashboardData.lastUpdated && (
            <span> • Last updated: {dashboardData.lastUpdated.toLocaleTimeString()}</span>
          )}
        </p>
      </div>

      {/* Welcome Card */}
      <div className="welcome-card slide-up">
        <h2>Good {new Date().getHours() < 12 ? 'Morning' : new Date().getHours() < 18 ? 'Afternoon' : 'Evening'}! 👋</h2>
        <p>
          You have {metrics.posts.scheduledPosts} posts scheduled and {metrics.overview.totalFollowers.toLocaleString()} total followers across platforms.
        </p>
      </div>

      {/* Error Display */}
      {dashboardData.error && (
        <div className="card" style={{backgroundColor: '#fef2f2', borderColor: '#fecaca', marginBottom: '2rem'}}>
          <div style={{color: '#dc2626', display: 'flex', alignItems: 'center', gap: '0.5rem'}}>
            <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
            </svg>
            <strong>Notice:</strong> {dashboardData.error}
          </div>
        </div>
      )}

      {/* Key Metrics Overview */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8 slide-up stagger-1">
        {/* Total Followers */}
        <div className="card hover-lift">
          <div style={{display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '1rem'}}>
            <div>
              <div className="stat-label">Total Followers</div>
              <div className="stat-value">{metrics.overview.totalFollowers.toLocaleString()}</div>
            </div>
            <div className="action-icon analytics">
              <UsersIcon className="w-6 h-6" />
            </div>
          </div>
          <div style={{fontSize: '0.875rem', color: 'var(--gray-600)'}}>
            Across all platforms
          </div>
        </div>

        {/* Total Engagement */}
        <div className="card hover-lift">
          <div style={{display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '1rem'}}>
            <div>
              <div className="stat-label">Total Engagement</div>
              <div className="stat-value">{metrics.overview.totalEngagement.toLocaleString()}</div>
            </div>
            <div className="action-icon create">
              <HeartIcon className="w-6 h-6" />
            </div>
          </div>
          <div style={{fontSize: '0.875rem', color: 'var(--gray-600)'}}>
            Avg rate: {metrics.overview.avgEngagementRate.toFixed(2)}%
          </div>
        </div>

        {/* Total Reach */}
        <div className="card hover-lift">
          <div style={{display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '1rem'}}>
            <div>
              <div className="stat-label">Total Reach</div>
              <div className="stat-value">{metrics.overview.totalReach.toLocaleString()}</div>
            </div>
            <div className="action-icon scheduled">
              <EyeIcon className="w-6 h-6" />
            </div>
          </div>
          <div style={{fontSize: '0.875rem', color: 'var(--gray-600)'}}>
            Last 30 days
          </div>
        </div>

        {/* Scheduled Posts */}
        <div className="card hover-lift">
          <div style={{display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '1rem'}}>
            <div>
              <div className="stat-label">Scheduled Posts</div>
              <div className="stat-value">{metrics.posts.scheduledPosts}</div>
            </div>
            <div className="action-icon calendar">
              <ClockIcon className="w-6 h-6" />
            </div>
          </div>
          <div style={{fontSize: '0.875rem', color: 'var(--gray-600)'}}>
            {metrics.posts.publishedPosts} published
          </div>
        </div>
      </div>

      {/* Platform Insights */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8 slide-up stagger-2">
        {/* Instagram Insights */}
        <div className="card platform-card instagram hover-lift">
          <div style={{display: 'flex', alignItems: 'center', marginBottom: '1.5rem'}}>
            <div className="platform-icon instagram">
              <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
                <path d="M12 2.163c3.204 0 3.584.012 4.85.07 3.252.148 4.771 1.691 4.919 4.919.058 1.265.069 1.645.069 4.849 0 3.205-.012 3.584-.069 4.849-.149 3.225-1.664 4.771-4.919 4.919-1.266.058-1.644.07-4.85.07-3.204 0-3.584-.012-4.849-.07-3.26-.149-4.771-1.699-4.919-4.92-.058-1.265-.07-1.644-.07-4.849 0-3.204.013-3.583.07-4.849.149-3.227 1.664-4.771 4.919-4.919 1.266-.057 1.645-.069 4.849-.069zm0-2.163c-3.259 0-3.667.014-4.947.072-4.358.2-6.78 2.618-6.98 6.98-.059 1.281-.073 1.689-.073 4.948 0 3.259.014 3.668.072 4.948.2 4.358 2.618 6.78 6.98 6.98 1.281.058 1.689.072 4.948.072 3.259 0 3.668-.014 4.948-.072 4.354-.2 6.782-2.618 6.979-6.98.059-1.28.073-1.689.073-4.948 0-3.259-.014-3.667-.072-4.947-.196-4.354-2.617-6.78-6.979-6.98-1.281-.059-1.69-.073-4.949-.073zm0 5.838c-3.403 0-6.162 2.759-6.162 6.162s2.759 6.163 6.162 6.163 6.162-2.759 6.162-6.163c0-3.403-2.759-6.162-6.162-6.162zm0 10.162c-2.209 0-4-1.79-4-4 0-2.209 1.791-4 4-4s4 1.791 4 4c0 2.21-1.791 4-4 4zm6.406-11.845c-.796 0-1.441.645-1.441 1.44s.645 1.44 1.441 1.44c.795 0 1.439-.645 1.439-1.44s-.644-1.44-1.439-1.44z"/>
              </svg>
            </div>
            <h2>Instagram Performance</h2>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div className="stat-card">
              <div className="stat-value">{metrics.instagram.followers.toLocaleString()}</div>
              <div className="stat-label">Followers</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{metrics.instagram.engagementRate.toFixed(2)}%</div>
              <div className="stat-label">Engagement Rate</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{metrics.instagram.reach.toLocaleString()}</div>
              <div className="stat-label">Reach</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{metrics.instagram.posts}</div>
              <div className="stat-label">Posts</div>
            </div>
          </div>
        </div>

        {/* Facebook Insights */}
        <div className="card platform-card facebook hover-lift">
          <div style={{display: 'flex', alignItems: 'center', marginBottom: '1.5rem'}}>
            <div className="platform-icon facebook">
              <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
                <path d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z"/>
              </svg>
            </div>
            <h2>Facebook Performance</h2>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div className="stat-card">
              <div className="stat-value">{metrics.facebook.followers.toLocaleString()}</div>
              <div className="stat-label">Followers</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{metrics.facebook.engagementRate.toFixed(2)}%</div>
              <div className="stat-label">Engagement Rate</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{metrics.facebook.reach.toLocaleString()}</div>
              <div className="stat-label">Reach</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{metrics.facebook.posts}</div>
              <div className="stat-label">Posts</div>
            </div>
          </div>

          {dashboardData.analytics.summary?.platform_breakdown?.facebook?.status && (
            <div style={{
              marginTop: '1rem',
              padding: '0.75rem',
              backgroundColor: dashboardData.analytics.summary.platform_breakdown.facebook.status === 'Connected' ? '#d1fae5' : '#fef3c7',
              borderRadius: 'var(--border-radius)',
              fontSize: '0.875rem',
              fontWeight: '500',
              color: dashboardData.analytics.summary.platform_breakdown.facebook.status === 'Connected' ? '#065f46' : '#92400e',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem'
            }}>
              {dashboardData.analytics.summary.platform_breakdown.facebook.status === 'Connected' ? (
                <span>✅ Connected</span>
              ) : (
                <span>ℹ️ {dashboardData.analytics.summary.platform_breakdown.facebook.status}</span>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Recent Posts & Quick Actions */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8 slide-up stagger-3">
        {/* Recent Posts */}
        <div className="card col-span-2 hover-lift">
          <h2>📝 Recent Posts</h2>
          {recentPosts.length > 0 ? (
            <div className="recent-posts">
              {recentPosts.map((post, index) => {
                // Handle platform display with deduplication
                let platformDisplay = post.platforms || post.platform || 'Instagram';
                if (typeof platformDisplay === 'string' && platformDisplay.includes(',')) {
                  // Remove duplicates from comma-separated platforms
                  const platforms = platformDisplay.split(',').map(p => p.trim());
                  const uniquePlatforms = Array.from(new Set(platforms));
                  platformDisplay = uniquePlatforms.join(', ');
                }

                // Format date in user's local timezone
                const formatLocalTime = (dateString) => {
                  if (!dateString) return 'N/A';
                  try {
                    const date = new Date(dateString);
                    return date.toLocaleString('en-US', {
                      month: 'short',
                      day: 'numeric',
                      year: 'numeric',
                      hour: 'numeric',
                      minute: '2-digit',
                      hour12: true,
                      timeZoneName: 'short'
                    });
                  } catch (error) {
                    console.error('Error formatting date:', error);
                    return dateString;
                  }
                };

                return (
                  <div key={post.id || `post-${index}`} className="post-item">
                    {post.media_urls && post.media_urls[0] && (
                      <img 
                        src={post.media_urls[0]} 
                        alt="Post media" 
                        className="post-image"
                        onError={(e) => {
                          e.target.onerror = null;
                          e.target.src = 'https://via.placeholder.com/48x48/e5e7eb/6b7280?text=📷';
                        }}
                      />
                    )}
                    <div className="post-content">
                      <div className="post-caption">
                        {post.caption ? post.caption.substring(0, 80) + (post.caption.length > 80 ? '...' : '') : 'No caption'}
                      </div>
                      <div className="post-meta">
                        <span>{formatLocalTime(post.scheduled_time || post.created_at)}</span>
                        <span>•</span>
                        <span>{platformDisplay}</span>
                      </div>
                    </div>
                    <div className={`post-status ${post.status || 'draft'}`}>
                      {post.status || 'draft'}
                    </div>
                  </div>
                );
              })}
            </div>
          ) : (
            <div className="empty-state">
              <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
              </svg>
              <h3>No posts yet</h3>
              <p>Create your first post to get started!</p>
            </div>
          )}
        </div>

        {/* Posts Overview */}
        <div className="card hover-lift">
          <h2>📊 Posts Overview</h2>
          <div className="grid grid-cols-1 gap-4">
            <div className="stat-card">
              <div className="stat-value">{metrics.posts.totalPosts}</div>
              <div className="stat-label">Total Posts</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{metrics.posts.scheduledPosts}</div>
              <div className="stat-label">Scheduled</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{metrics.posts.publishedPosts}</div>
              <div className="stat-label">Published</div>
            </div>
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="slide-up stagger-4">
        <h2 style={{marginBottom: '1.5rem', color: 'var(--gray-900)', fontSize: '1.5rem', fontWeight: '700'}}>
          🚀 Quick Actions
        </h2>
        <div className="quick-actions">
          <Link to="/scheduler" className="action-card">
            <div className="action-icon create">
              <PhotoIcon className="w-6 h-6" />
            </div>
            <h3>Create Post</h3>
            <p>Upload media and schedule new content across your social platforms</p>
          </Link>

          <Link to="/scheduled" className="action-card">
            <div className="action-icon scheduled">
              <ClockIcon className="w-6 h-6" />
            </div>
            <h3>Scheduled Posts</h3>
            <p>View and manage your upcoming scheduled posts</p>
          </Link>

          <Link to="/analytics" className="action-card">
            <div className="action-icon analytics">
              <ChartBarIcon className="w-6 h-6" />
            </div>
            <h3>Analytics</h3>
            <p>Track performance metrics and insights across platforms</p>
          </Link>

          <Link to="/calendar" className="action-card">
            <div className="action-icon calendar">
              <CalendarDaysIcon className="w-6 h-6" />
            </div>
            <h3>Content Calendar</h3>
            <p>Plan and organize your content strategy with calendar view</p>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
