import React, { useEffect, useState } from 'react';
import { Line, Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';
import apiClient from '../api';
import '../styles/analytics.css';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Analytics = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [selectedPeriod, setSelectedPeriod] = useState('30d');
  const [analyticsData, setAnalyticsData] = useState({
    summary: null,
    growth: null,
    instagram: null,
    facebook: null,
    engagement: null,
    reach: null
  });

  // Fetch all analytics data
  const fetchAnalytics = async (period = '30d') => {
    setLoading(true);
    setError(null);

    try {
      console.log(`Fetching analytics for period: ${period}`);

      // Fetch comprehensive analytics data in parallel
      const [
        summaryResponse,
        growthResponse,
        generalResponse,
        engagementResponse,
        reachResponse
      ] = await Promise.all([
        apiClient.getAnalyticsSummary(period),
        apiClient.getGrowthComparison(period),
        apiClient.getAnalytics('all', period),
        apiClient.getEngagementStats('all'),
        apiClient.getReachStats('all')
      ]);

      console.log('Summary analytics:', summaryResponse);
      console.log('Growth comparison:', growthResponse);
      console.log('General analytics:', generalResponse);
      console.log('Engagement analytics:', engagementResponse);
      console.log('Reach analytics:', reachResponse);

      setAnalyticsData({
        summary: summaryResponse.data || {},
        growth: growthResponse.data || {},
        instagram: generalResponse.data?.instagram || {},
        facebook: generalResponse.data?.facebook || {},
        engagement: engagementResponse.data || {},
        reach: reachResponse.data || {}
      });

      setLastUpdated(new Date());
      setError(null);
    } catch (err) {
      console.error('Error fetching analytics:', err);
      setError('Failed to load analytics data. Please check your API configuration and try again.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAnalytics(selectedPeriod);
  }, [selectedPeriod]);

  // Auto-refresh data every 5 minutes
  useEffect(() => {
    const interval = setInterval(() => {
      fetchAnalytics(selectedPeriod);
    }, 5 * 60 * 1000); // 5 minutes

    return () => clearInterval(interval);
  }, [selectedPeriod]);

  const handlePeriodChange = (period) => {
    setSelectedPeriod(period);
  };

  const handleRefresh = () => {
    fetchAnalytics(selectedPeriod);
  };

  if (loading) {
    return (
      <div className="analytics-dashboard">
        <div className="flex justify-center items-center h-64">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto mb-4"></div>
            <div className="text-lg font-medium text-gray-600">Loading analytics data...</div>
            <div className="text-sm text-gray-500 mt-2">This may take a few moments</div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="analytics-dashboard">
        <div className="bg-red-50 border border-red-200 text-red-700 px-6 py-4 rounded-lg" role="alert">
          <div className="flex items-center">
            <svg className="w-5 h-5 mr-2" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
            </svg>
            <div>
              <strong className="font-bold">Error!</strong>
              <span className="block sm:inline ml-1">{error}</span>
            </div>
          </div>
          <button 
            onClick={handleRefresh}
            className="mt-3 bg-red-100 hover:bg-red-200 text-red-800 px-4 py-2 rounded-md text-sm font-medium transition-colors"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  // Process follower trend data
  const growthData = analyticsData.growth || {};
  const instagramGrowth = growthData.instagram || { dates: [], follower_counts: [] };
  const facebookGrowth = growthData.facebook || { dates: [], follower_counts: [] };

  // Create follower trend chart data
  const followerTrendData = {
    labels: instagramGrowth.dates?.map(date => 
      new Date(date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })
    ) || [],
    datasets: [
      {
        label: 'Instagram Followers',
        data: instagramGrowth.follower_counts || [],
        borderColor: '#E4405F',
        backgroundColor: 'rgba(228, 64, 95, 0.1)',
        tension: 0.3,
        fill: true
      },
      {
        label: 'Facebook Followers',
        data: facebookGrowth.follower_counts || [],
        borderColor: '#1877F2',
        backgroundColor: 'rgba(24, 119, 242, 0.1)',
        tension: 0.3,
        fill: true
      }
    ],
  };

  // Process engagement data
  const instagramEngagement = analyticsData.engagement?.instagram || {};
  const facebookEngagement = analyticsData.engagement?.facebook || {};

  // Create engagement comparison chart
  const engagementComparisonData = {
    labels: ['Likes', 'Comments', 'Shares', 'Saves'],
    datasets: [
      {
        label: 'Instagram',
        data: [
          instagramEngagement.avg_likes || 0,
          instagramEngagement.avg_comments || 0,
          instagramEngagement.avg_shares || 0,
          instagramEngagement.avg_saves || 0
        ],
        backgroundColor: 'rgba(228, 64, 95, 0.8)',
      },
      {
        label: 'Facebook',
        data: [
          facebookEngagement.avg_likes || 0,
          facebookEngagement.avg_comments || 0,
          facebookEngagement.avg_shares || 0,
          facebookEngagement.avg_reactions || 0
        ],
        backgroundColor: 'rgba(24, 119, 242, 0.8)',
      }
    ],
  };

  // Best times to post data
  const instagramBestTimes = instagramEngagement.best_times || {};
  const facebookBestTimes = facebookEngagement.best_times || {};

  const bestTimesData = {
    labels: Array.from({length: 24}, (_, i) => `${i}:00`),
    datasets: [
      {
        label: 'Instagram Engagement Score',
        data: Array.from({length: 24}, (_, i) => instagramBestTimes[i] || 0),
        borderColor: '#E4405F',
        backgroundColor: 'rgba(228, 64, 95, 0.1)',
        tension: 0.3
      },
      {
        label: 'Facebook Engagement Score',
        data: Array.from({length: 24}, (_, i) => facebookBestTimes[i] || 0),
        borderColor: '#1877F2',
        backgroundColor: 'rgba(24, 119, 242, 0.1)',
        tension: 0.3
      }
    ],
  };

  // Platform distribution data
  const platformDistributionData = {
    labels: ['Instagram', 'Facebook'],
    datasets: [
      {
        data: [
          analyticsData.summary?.platform_breakdown?.instagram?.engagement || 0,
          analyticsData.summary?.platform_breakdown?.facebook?.engagement || 0,
        ],
        backgroundColor: ['#E4405F', '#1877F2'],
        borderWidth: 0
      },
    ],
  };

  // Extract key metrics with fallbacks
  const metrics = {
    overview: {
      totalFollowers: analyticsData.summary?.overview?.total_followers || 0,
      totalEngagement: analyticsData.summary?.overview?.total_engagement || 0,
      totalReach: analyticsData.summary?.overview?.total_reach || 0,
      avgEngagementRate: analyticsData.summary?.overview?.avg_engagement_rate || 0,
      followerGrowth: analyticsData.summary?.overview?.follower_growth || 0,
      totalPosts: analyticsData.summary?.overview?.total_posts || 0
    },
    instagram: {
      followers: analyticsData.summary?.platform_breakdown?.instagram?.followers || 
                analyticsData.instagram?.total_followers_gained || 0,
      engagementRate: analyticsData.summary?.platform_breakdown?.instagram?.engagement_rate ||
                     analyticsData.instagram?.avg_engagement_rate || 0,
      totalEngagement: analyticsData.summary?.platform_breakdown?.instagram?.engagement ||
                      analyticsData.instagram?.total_engagement || 0,
      reach: analyticsData.summary?.platform_breakdown?.instagram?.reach ||
             analyticsData.reach?.instagram?.total_reach || 0,
      posts: analyticsData.summary?.platform_breakdown?.instagram?.posts ||
             analyticsData.instagram?.total_posts || 0,
      growthRate: analyticsData.growth?.instagram?.growth_rate || 0
    },
    facebook: {
      followers: analyticsData.summary?.platform_breakdown?.facebook?.followers ||
                analyticsData.facebook?.total_followers_gained || 0,
      engagementRate: analyticsData.summary?.platform_breakdown?.facebook?.engagement_rate ||
                     analyticsData.facebook?.engagement_rate || 0,
      totalEngagement: analyticsData.summary?.platform_breakdown?.facebook?.engagement ||
                      analyticsData.facebook?.total_engagement || 0,
      reach: analyticsData.summary?.platform_breakdown?.facebook?.reach ||
             analyticsData.reach?.facebook?.total_reach || 0,
      posts: analyticsData.summary?.platform_breakdown?.facebook?.posts ||
             analyticsData.facebook?.total_posts || 0,
      growthRate: analyticsData.growth?.facebook?.growth_rate || 0
    }
  };

  return (
    <div className="analytics-dashboard space-y-8 p-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">📊 Analytics Dashboard</h1>
          {lastUpdated && (
            <p className="text-sm text-gray-500 mt-1">
              Last updated: {lastUpdated.toLocaleString()} • Auto-refreshes every 5 minutes
            </p>
          )}
        </div>

        <div className="flex items-center gap-3">
          {/* Period Selector */}
          <select 
            value={selectedPeriod} 
            onChange={(e) => handlePeriodChange(e.target.value)}
            className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent text-sm"
          >
            <option value="7d">Last 7 days</option>
            <option value="30d">Last 30 days</option>
            <option value="90d">Last 90 days</option>
          </select>

          {/* Refresh Button */}
          <button
            onClick={handleRefresh}
            className="px-4 py-2 bg-primary-600 hover:bg-primary-700 text-white rounded-lg transition-colors text-sm font-medium flex items-center gap-2"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
            </svg>
            Refresh
          </button>
        </div>
      </div>

      {/* Key Metrics Overview */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {/* Total Followers */}
        <div className="metric-card bg-gradient-to-r from-pink-500 to-purple-600 text-white">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-pink-100 text-sm font-medium">Total Followers</p>
              <p className="text-3xl font-bold">{metrics.overview.totalFollowers.toLocaleString()}</p>
            </div>
            <div className="w-12 h-12 bg-white bg-opacity-20 rounded-lg flex items-center justify-center">
              <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24">
                <path d="M16 4c0-1.11.89-2 2-2s2 .89 2 2-.89 2-2 2-2-.89-2-2zm4 18v-6h2.5l-2.54-7.63A1.5 1.5 0 0 0 18.5 7h-5c-.8 0-1.5.7-1.5 1.5v6c0 .8.7 1.5 1.5 1.5H16v6h4zM12.5 11.5c.8 0 1.5-.7 1.5-1.5s-.7-1.5-1.5-1.5S11 9.2 11 10s.7 1.5 1.5 1.5zM7 22v-6H4.5l2.54-7.63A1.5 1.5 0 0 1 8.5 7h5c.8 0 1.5.7 1.5 1.5v6c0 .8-.7 1.5-1.5 1.5H11v6H7z"/>
              </svg>
            </div>
          </div>
          <div className="mt-3 flex justify-between text-sm">
            <span className="text-pink-100">
              Growth: {metrics.overview.followerGrowth > 0 ? '+' : ''}{metrics.overview.followerGrowth}
            </span>
            <span className="text-pink-100">{metrics.overview.totalPosts} posts</span>
          </div>
        </div>

        {/* Instagram Performance */}
        <div className="metric-card bg-gradient-to-r from-orange-500 to-pink-600 text-white">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-orange-100 text-sm font-medium">Instagram</p>
              <p className="text-3xl font-bold">{metrics.instagram.followers.toLocaleString()}</p>
            </div>
            <div className="w-12 h-12 bg-white bg-opacity-20 rounded-lg flex items-center justify-center">
              <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24">
                <path d="M12 2.163c3.204 0 3.584.012 4.85.07 3.252.148 4.771 1.691 4.919 4.919.058 1.265.069 1.645.069 4.849 0 3.205-.012 3.584-.069 4.849-.149 3.225-1.664 4.771-4.919 4.919-1.266.058-1.644.07-4.85.07-3.204 0-3.584-.012-4.849-.07-3.26-.149-4.771-1.699-4.919-4.92-.058-1.265-.07-1.644-.07-4.849 0-3.204.013-3.583.07-4.849.149-3.227 1.664-4.771 4.919-4.919 1.266-.057 1.645-.069 4.849-.069zm0-2.163c-3.259 0-3.667.014-4.947.072-4.358.2-6.78 2.618-6.98 6.98-.059 1.281-.073 1.689-.073 4.948 0 3.259.014 3.668.072 4.948.2 4.358 2.618 6.78 6.98 6.98 1.281.058 1.689.072 4.948.072 3.259 0 3.668-.014 4.948-.072 4.354-.2 6.782-2.618 6.979-6.98.059-1.28.073-1.689.073-4.948 0-3.259-.014-3.667-.072-4.947-.196-4.354-2.617-6.78-6.979-6.98-1.281-.059-1.69-.073-4.949-.073zm0 5.838c-3.403 0-6.162 2.759-6.162 6.162s2.759 6.163 6.162 6.163 6.162-2.759 6.162-6.163c0-3.403-2.759-6.162-6.162-6.162zm0 10.162c-2.209 0-4-1.79-4-4 0-2.209 1.791-4 4-4s4 1.791 4 4c0 2.21-1.791 4-4 4zm6.406-11.845c-.796 0-1.441.645-1.441 1.44s.645 1.44 1.441 1.44c.795 0 1.439-.645 1.439-1.44s-.644-1.44-1.439-1.44z"/>
              </svg>
            </div>
          </div>
          <div className="mt-3 flex justify-between text-sm">
            <span className="text-orange-100">
              {metrics.instagram.engagementRate.toFixed(2)}% engagement
            </span>
            <span className="text-orange-100">
              {metrics.instagram.growthRate > 0 ? '+' : ''}{metrics.instagram.growthRate.toFixed(1)}%
            </span>
          </div>
        </div>

        {/* Facebook Performance */}
        <div className="metric-card bg-gradient-to-r from-blue-500 to-blue-700 text-white">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-blue-100 text-sm font-medium">Facebook</p>
              <p className="text-3xl font-bold">{metrics.facebook.followers.toLocaleString()}</p>
            </div>
            <div className="w-12 h-12 bg-white bg-opacity-20 rounded-lg flex items-center justify-center">
              <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24">
                <path d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z"/>
              </svg>
            </div>
          </div>
          <div className="mt-3 flex justify-between text-sm">
            <span className="text-blue-100">
              {metrics.facebook.engagementRate.toFixed(2)}% engagement
            </span>
            <span className="text-blue-100">
              {metrics.facebook.growthRate > 0 ? '+' : ''}{metrics.facebook.growthRate.toFixed(1)}%
            </span>
          </div>
        </div>

        {/* Total Reach & Engagement */}
        <div className="metric-card bg-gradient-to-r from-green-500 to-emerald-600 text-white">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-green-100 text-sm font-medium">Total Reach</p>
              <p className="text-3xl font-bold">{metrics.overview.totalReach.toLocaleString()}</p>
            </div>
            <div className="w-12 h-12 bg-white bg-opacity-20 rounded-lg flex items-center justify-center">
              <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24">
                <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z"/>
              </svg>
            </div>
          </div>
          <div className="mt-3">
            <p className="text-sm text-green-100">
              {metrics.overview.totalEngagement.toLocaleString()} total engagements
            </p>
          </div>
        </div>
      </div>

      {/* Charts Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Follower Growth Trend */}
        <div className="chart-container">
          <h3 className="chart-title">📈 Follower Growth Trend</h3>
          {followerTrendData.labels.length > 0 ? (
            <Line 
              data={followerTrendData} 
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  legend: {
                    position: 'top',
                  },
                  tooltip: {
                    mode: 'index',
                    intersect: false,
                  }
                },
                scales: {
                  y: {
                    beginAtZero: false,
                    title: {
                      display: true,
                      text: 'Followers'
                    }
                  },
                  x: {
                    title: {
                      display: true,
                      text: 'Date'
                    }
                  }
                },
                interaction: {
                  mode: 'nearest',
                  axis: 'x',
                  intersect: false
                }
              }}
              height={300}
            />
          ) : (
            <div className="flex items-center justify-center h-64 text-gray-500">
              <div className="text-center">
                <svg className="w-12 h-12 mx-auto mb-2 text-gray-300" fill="currentColor" viewBox="0 0 24 24">
                  <path d="M3 3v18h18v-2H5V3H3z"/>
                  <path d="M21 7l-4-4-4 4-4-4-4 4v10l4-4 4 4 4-4 4 4V7z"/>
                </svg>
                <p>No follower data available</p>
              </div>
            </div>
          )}
        </div>

        {/* Engagement Comparison */}
        <div className="chart-container">
          <h3 className="chart-title">💬 Engagement Comparison</h3>
          <Bar 
            data={engagementComparisonData} 
            options={{
              responsive: true,
              maintainAspectRatio: false,
              plugins: {
                legend: {
                  position: 'top',
                },
              },
              scales: {
                y: {
                  beginAtZero: true,
                  title: {
                    display: true,
                    text: 'Average Count'
                  }
                }
              }
            }}
            height={300}
          />
        </div>

        {/* Best Times to Post */}
        <div className="chart-container">
          <h3 className="chart-title">⏰ Best Times to Post</h3>
          <Line 
            data={bestTimesData} 
            options={{
              responsive: true,
              maintainAspectRatio: false,
              plugins: {
                legend: {
                  position: 'top',
                },
              },
              scales: {
                y: {
                  beginAtZero: true,
                  title: {
                    display: true,
                    text: 'Engagement Score'
                  }
                },
                x: {
                  title: {
                    display: true,
                    text: 'Hour of Day'
                  }
                }
              }
            }}
            height={300}
          />
        </div>

        {/* Platform Distribution */}
        <div className="chart-container">
          <h3 className="chart-title">🏆 Engagement Distribution</h3>
          <div className="flex justify-center items-center h-64">
            <Doughnut 
              data={platformDistributionData} 
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  legend: {
                    position: 'bottom',
                  },
                  tooltip: {
                    callbacks: {
                      label: function(context) {
                        const label = context.label || '';
                        const value = context.parsed || 0;
                        const total = context.dataset.data.reduce((a, b) => a + b, 0);
                        const percentage = total > 0 ? ((value / total) * 100).toFixed(1) : 0;
                        return `${label}: ${value.toLocaleString()} (${percentage}%)`;
                      }
                    }
                  }
                }
              }}
            />
          </div>
        </div>
      </div>

      {/* Detailed Platform Insights */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Instagram Insights */}
        <div className="platform-insights">
          <div className="platform-header">
            <div className="w-8 h-8 bg-gradient-to-r from-pink-500 to-purple-600 rounded-lg flex items-center justify-center mr-3">
              <svg className="w-5 h-5 text-white" fill="currentColor" viewBox="0 0 24 24">
                <path d="M12 2.163c3.204 0 3.584.012 4.85.07 3.252.148 4.771 1.691 4.919 4.919.058 1.265.069 1.645.069 4.849 0 3.205-.012 3.584-.069 4.849-.149 3.225-1.664 4.771-4.919 4.919-1.266.058-1.644.07-4.85.07-3.204 0-3.584-.012-4.849-.07-3.26-.149-4.771-1.699-4.919-4.92-.058-1.265-.07-1.644-.07-4.849 0-3.204.013-3.583.07-4.849.149-3.227 1.664-4.771 4.919-4.919 1.266-.057 1.645-.069 4.849-.069zm0-2.163c-3.259 0-3.667.014-4.947.072-4.358.2-6.78 2.618-6.98 6.98-.059 1.281-.073 1.689-.073 4.948 0 3.259.014 3.668.072 4.948.2 4.358 2.618 6.78 6.98 6.98 1.281.058 1.689.072 4.948.072 3.259 0 3.668-.014 4.948-.072 4.354-.2 6.782-2.618 6.979-6.98.059-1.28.073-1.689.073-4.948 0-3.259-.014-3.667-.072-4.947-.196-4.354-2.617-6.78-6.979-6.98-1.281-.059-1.69-.073-4.949-.073zm0 5.838c-3.403 0-6.162 2.759-6.162 6.162s2.759 6.163 6.162 6.163 6.162-2.759 6.162-6.163c0-3.403-2.759-6.162-6.162-6.162zm0 10.162c-2.209 0-4-1.79-4-4 0-2.209 1.791-4 4-4s4 1.791 4 4c0 2.21-1.791 4-4 4zm6.406-11.845c-.796 0-1.441.645-1.441 1.44s.645 1.44 1.441 1.44c.795 0 1.439-.645 1.439-1.44s-.644-1.44-1.439-1.44z"/>
              </svg>
            </div>
            <h3 className="text-xl font-semibold text-gray-900">Instagram Performance</h3>
          </div>

          <div className="space-y-3">
            <div className="insight-item">
              <span className="insight-label">Followers</span>
              <span className="insight-value">{metrics.instagram.followers.toLocaleString()}</span>
            </div>
            <div className="insight-item">
              <span className="insight-label">Engagement Rate</span>
              <span className="insight-value">{metrics.instagram.engagementRate.toFixed(2)}%</span>
            </div>
            <div className="insight-item">
              <span className="insight-label">Total Reach</span>
              <span className="insight-value">{metrics.instagram.reach.toLocaleString()}</span>
            </div>
            <div className="insight-item">
              <span className="insight-label">Total Engagement</span>
              <span className="insight-value">{metrics.instagram.totalEngagement.toLocaleString()}</span>
            </div>
            <div className="insight-item">
              <span className="insight-label">Growth Rate</span>
              <span className="insight-value text-green-600">
                {metrics.instagram.growthRate > 0 ? '+' : ''}{metrics.instagram.growthRate.toFixed(1)}%
              </span>
            </div>
          </div>
        </div>

        {/* Facebook Insights */}
        <div className="platform-insights">
          <div className="platform-header">
            <div className="w-8 h-8 bg-blue-600 rounded-lg flex items-center justify-center mr-3">
              <svg className="w-5 h-5 text-white" fill="currentColor" viewBox="0 0 24 24">
                <path d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z"/>
              </svg>
            </div>
            <h3 className="text-xl font-semibold text-gray-900">Facebook Performance</h3>
          </div>

          <div className="space-y-3">
            <div className="insight-item">
              <span className="insight-label">Followers</span>
              <span className="insight-value">{metrics.facebook.followers.toLocaleString()}</span>
            </div>
            <div className="insight-item">
              <span className="insight-label">Engagement Rate</span>
              <span className="insight-value">{metrics.facebook.engagementRate.toFixed(2)}%</span>
            </div>
            <div className="insight-item">
              <span className="insight-label">Total Reach</span>
              <span className="insight-value">{metrics.facebook.reach.toLocaleString()}</span>
            </div>
            <div className="insight-item">
              <span className="insight-label">Total Engagement</span>
              <span className="insight-value">{metrics.facebook.totalEngagement.toLocaleString()}</span>
            </div>
            <div className="insight-item">
              <span className="insight-label">Growth Rate</span>
              <span className="insight-value text-green-600">
                {metrics.facebook.growthRate > 0 ? '+' : ''}{metrics.facebook.growthRate.toFixed(1)}%
              </span>
            </div>
          </div>
        </div>
      </div>

      {/* Growth Comparison Summary */}
      {analyticsData.growth?.comparison && (
        <div className="bg-white rounded-lg p-6 shadow-sm border border-gray-200">
          <h3 className="text-xl font-semibold text-gray-900 mb-4">📊 Growth Summary</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="text-center p-4 bg-gray-50 rounded-lg">
              <p className="text-sm text-gray-600 mb-1">Better Performing</p>
              <p className="text-lg font-bold capitalize">
                {analyticsData.growth.comparison.better_performing_platform}
              </p>
            </div>
            <div className="text-center p-4 bg-gray-50 rounded-lg">
              <p className="text-sm text-gray-600 mb-1">Growth Difference</p>
              <p className="text-lg font-bold">
                {analyticsData.growth.comparison.growth_difference.toFixed(1)}%
              </p>
            </div>
            <div className="text-center p-4 bg-gray-50 rounded-lg">
              <p className="text-sm text-gray-600 mb-1">Combined Growth</p>
              <p className="text-lg font-bold text-green-600">
                +{analyticsData.growth.comparison.combined_growth.toFixed(1)}%
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Analytics;
