import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  PlusCircleIcon, 
  ClockIcon, 
  CalendarIcon, 
  ChartBarIcon 
} from '@heroicons/react/24/outline';
import apiClient from '../api';
import toast from 'react-hot-toast';

const PostScheduler = () => {
  const [stats, setStats] = useState({
    scheduledPosts: 0,
    totalPosts: 0,
    isLoading: true
  });

  useEffect(() => {
    loadStats();
  }, []);

  const loadStats = async () => {
    try {
      const response = await apiClient.getScheduledPosts();

      // Handle different response formats
      let posts = [];
      if (Array.isArray(response)) {
        posts = response;
      } else if (response && Array.isArray(response.posts)) {
        posts = response.posts;
      } else {
        console.warn('Unexpected response format from API:', response);
        posts = [];
      }

      setStats({
        scheduledPosts: posts.filter(p => p.status === 'scheduled').length,
        totalPosts: posts.length,
        isLoading: false
      });
    } catch (error) {
      console.error('Error loading stats:', error);
      toast.error('Failed to load dashboard stats');
      setStats(prev => ({ ...prev, isLoading: false }));
    }
  };

  const quickActions = [
    {
      title: 'Schedule New Post',
      description: 'Upload media and schedule your next social media post',
      icon: PlusCircleIcon,
      link: '/scheduler',
      color: 'bg-primary-500 hover:bg-primary-600'
    },
    {
      title: 'View Scheduled Posts',
      description: 'Manage your upcoming scheduled posts',
      icon: ClockIcon,
      link: '/scheduled',
      color: 'bg-green-500 hover:bg-green-600'
    },
    {
      title: 'Content Calendar',
      description: 'Generate AI-powered content calendar',
      icon: CalendarIcon,
      link: '/calendar',
      color: 'bg-purple-500 hover:bg-purple-600'
    },
    {
      title: 'Analytics',
      description: 'View your social media performance',
      icon: ChartBarIcon,
      link: '/analytics',
      color: 'bg-orange-500 hover:bg-orange-600'
    }
  ];

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="text-center">
        <h1 className="text-3xl font-bold text-gray-900 mb-4">
          Welcome to Social Media Assistant
        </h1>
        <p className="text-lg text-gray-600">
          Manage your Instagram and Facebook content with AI-powered tools
        </p>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="card">
          <div className="flex items-center">
            <div className="p-2 bg-primary-100 rounded-lg">
              <ClockIcon className="h-6 w-6 text-primary-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Scheduled Posts</p>
              <p className="text-2xl font-bold text-gray-900">
                {stats.isLoading ? '...' : stats.scheduledPosts}
              </p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center">
            <div className="p-2 bg-green-100 rounded-lg">
              <ChartBarIcon className="h-6 w-6 text-green-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Total Posts</p>
              <p className="text-2xl font-bold text-gray-900">
                {stats.isLoading ? '...' : stats.totalPosts}
              </p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center">
            <div className="p-2 bg-purple-100 rounded-lg">
              <CalendarIcon className="h-6 w-6 text-purple-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">This Month</p>
              <p className="text-2xl font-bold text-gray-900">
                {new Date().toLocaleDateString('en-US', { month: 'long' })}
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div>
        <h2 className="text-2xl font-bold text-gray-900 mb-6">Quick Actions</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {quickActions.map((action, index) => (
            <Link
              key={index}
              to={action.link}
              className="group block"
            >
              <div className="card hover:shadow-md transition-shadow duration-200">
                <div className="flex items-center mb-4">
                  <div className={`p-3 rounded-lg ${action.color} text-white`}>
                    <action.icon className="h-6 w-6" />
                  </div>
                </div>
                <h3 className="text-lg font-semibold text-gray-900 mb-2">
                  {action.title}
                </h3>
                <p className="text-gray-600 text-sm">
                  {action.description}
                </p>
                <div className="mt-4">
                  <span className="text-primary-600 text-sm font-medium group-hover:text-primary-700">
                    Get started â†’
                  </span>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </div>

      {/* Recent Activity */}
      <div className="card">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">
          Getting Started
        </h3>
        <div className="space-y-3">
          <div className="flex items-center text-sm">
            <div className="w-2 h-2 bg-primary-500 rounded-full mr-3"></div>
            <span className="text-gray-600">Upload your media files (images or videos)</span>
          </div>
          <div className="flex items-center text-sm">
            <div className="w-2 h-2 bg-primary-500 rounded-full mr-3"></div>
            <span className="text-gray-600">Generate AI-powered captions</span>
          </div>
          <div className="flex items-center text-sm">
            <div className="w-2 h-2 bg-primary-500 rounded-full mr-3"></div>
            <span className="text-gray-600">Schedule your posts for optimal engagement</span>
          </div>
          <div className="flex items-center text-sm">
            <div className="w-2 h-2 bg-primary-500 rounded-full mr-3"></div>
            <span className="text-gray-600">Track your analytics and performance</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PostScheduler;
