import React, { useEffect, useState } from 'react';
import { Line, Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';
import '../styles/analytics.css';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Analytics = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [selectedPeriod, setSelectedPeriod] = useState('30d');
  const [analyticsData, setAnalyticsData] = useState({
    summary: null,
    growth: null,
    instagram: null,
    facebook: null,
    engagement: null,
    reach: null,
    best_times: null,
    top_posts: null
  });

  // Fetch all analytics data
  const fetchAnalytics = async (period = '30d') => {
      setLoading(true);
    setError(null);

    try {
      const timestamp = Date.now();
      const response = await fetch(`/api/analytics/summary?period=${period}&_t=${timestamp}`, {
        headers: {
          'Cache-Control': 'no-cache',
          'Pragma': 'no-cache'
        }
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      console.log('🔍 Fresh Analytics Data Received:', data);

      setAnalyticsData(data);
      setLastUpdated(new Date());

      } catch (err) {
      console.error('Analytics fetch error:', err);
      setError(err.message);
      } finally {
        setLoading(false);
      }
    };

  useEffect(() => {
    fetchAnalytics(selectedPeriod);
  }, [selectedPeriod]);

  const handlePeriodChange = (period) => {
    setSelectedPeriod(period);
  };

  const handleRefresh = () => {
    fetchAnalytics(selectedPeriod);
  };

  if (loading) {
    return (
      <div className="analytics-dashboard">
        <div className="analytics-loading fade-in">
          <div className="loading-spinner"></div>
          <div className="loading-text">Loading Analytics Dashboard</div>
          <div className="loading-subtext">Fetching your social media insights...</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="analytics-dashboard">
