                  {post.permalink && (
                    <a href={post.permalink} target="_blank" rel="noopener noreferrer" className="view-post-link">
                      View Post →
                    </a>
                  )}
                </div>
              </div>
            )) || (
              <div className="no-data">No top posts data available</div>
            )}
          </div>
        </div>

        {/* Facebook Top Posts */}
        <div className="top-posts-card hover-lift">
          <h3 className="top-posts-title facebook-text">🎯 Facebook Top Posts</h3>
          <div className="top-posts-list">
            {topPosts.facebook?.slice(0, 3).map((post, index) => (
              <div key={index} className="top-post-item">
                <div className="post-rank">#{index + 1}</div>
                <div className="post-info">
                  <p className="post-caption">{post.message?.substring(0, 60)}...</p>
                  <div className="post-metrics">
                    <span className="metric">👍 {post.likes}</span>
                    <span className="metric">💬 {post.comments}</span>
                    <span className="metric">🔄 {post.shares}</span>
                    <span className="metric">📊 {post.engagement_rate?.toFixed(1)}%</span>
                  </div>
                  {post.permalink_url && (
                    <a href={post.permalink_url} target="_blank" rel="noopener noreferrer" className="view-post-link">
                      View Post →
                    </a>
                  )}
                </div>
              </div>
            )) || (
              <div className="no-data">No top posts data available</div>
            )}
          </div>
        </div>
      </div>

      {/* Growth Comparison Summary */}
      <div className="growth-summary-card hover-lift slide-up" style={{ animationDelay: '1s' }}>
        <h3 className="growth-summary-title">📈 Growth Comparison Summary</h3>
        <div className="growth-comparison-grid">
          <div className="growth-metric">
            <span className="growth-label">Instagram Growth</span>
            <span className={`growth-value ${metrics.instagram.growthRate >= 0 ? 'text-success' : 'text-error'}`}>
              {metrics.instagram.growthRate > 0 ? '+' : ''}{metrics.instagram.growthRate.toFixed(1)}%
            </span>
          </div>
          <div className="growth-metric">
            <span className="growth-label">Facebook Growth</span>
            <span className={`growth-value ${metrics.facebook.growthRate >= 0 ? 'text-success' : 'text-error'}`}>
              {metrics.facebook.growthRate > 0 ? '+' : ''}{metrics.facebook.growthRate.toFixed(1)}%
            </span>
          </div>
          <div className="growth-metric">
            <span className="growth-label">Better Platform</span>
            <span className="growth-value">
              {metrics.instagram.growthRate > metrics.facebook.growthRate ? '📸 Instagram' : '📘 Facebook'}
            </span>
          </div>
          <div className="growth-metric">
            <span className="growth-label">Growth Difference</span>
            <span className="growth-value">
              {Math.abs(metrics.instagram.growthRate - metrics.facebook.growthRate).toFixed(1)}%
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Analytics;
