              </span>
            </div>
            {analyticsData.summary?.platform_breakdown?.facebook?.status && (
              <div className="insight-item bg-yellow-50 border-yellow-200">
                <span className="insight-label text-yellow-700">Status</span>
                <span className="insight-value text-yellow-800 text-sm">
                  {analyticsData.summary.platform_breakdown.facebook.status}
                </span>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Growth Comparison Summary */}
      {analyticsData.growth?.comparison && (
        <div className="growth-summary hover-lift slide-up" style={{ animationDelay: '0.6s' }}>
          <h3 className="text-xl font-semibold text-gray-900 mb-6 flex items-center">
             Growth Summary
            <span className="ml-2 text-sm font-normal text-gray-500">Platform Performance Comparison</span>
          </h3>
          <div className="growth-summary-grid">
            <div className="growth-summary-item">
              <p className="text-sm text-gray-600 font-semibold uppercase tracking-wide mb-2">Better Performing</p>
              <p className="text-lg font-bold capitalize text-gray-800">
                {analyticsData.growth.comparison.better_performing_platform}
              </p>
            </div>
            <div className="growth-summary-item">
              <p className="text-sm text-gray-600 font-semibold uppercase tracking-wide mb-2">Growth Difference</p>
              <p className="text-lg font-bold text-gray-800">
                {analyticsData.growth.comparison.growth_difference.toFixed(1)}%
              </p>
            </div>
            <div className="growth-summary-item">
              <p className="text-sm text-gray-600 font-semibold uppercase tracking-wide mb-2">Combined Growth</p>
              <p className="text-lg font-bold text-green-600">
                +{analyticsData.growth.comparison.combined_growth.toFixed(1)}%
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Analytics;
