import React, { useEffect, useState } from 'react';
import { Line, Bar, Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';
import apiClient from '../api';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Analytics = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [analyticsData, setAnalyticsData] = useState({
    instagram: null,
    facebook: null,
    instagramEngagement: null,
    facebookEngagement: null
  });

  useEffect(() => {
    const fetchAnalytics = async () => {
      setLoading(true);
      try {
        // Fetch general analytics
        const analyticsResponse = await apiClient.getAnalytics();
        console.log('Analytics response:', analyticsResponse);

        // Fetch engagement data
        const instagramEngagementResponse = await apiClient.getEngagementStats('instagram');
        console.log('Instagram engagement response:', instagramEngagementResponse);

        const facebookEngagementResponse = await apiClient.getEngagementStats('facebook');
        console.log('Facebook engagement response:', facebookEngagementResponse);

        // Extract Instagram data
        const instagramData = analyticsResponse.data?.instagram || {};

        // Extract Facebook data
        const facebookData = analyticsResponse.data?.facebook || {};

        // Extract engagement data
        const instagramEngagementData = instagramEngagementResponse.data?.instagram || {};
        const facebookEngagementData = facebookEngagementResponse.data?.facebook || {};

        setAnalyticsData({
          instagram: instagramData,
          facebook: facebookData,
          instagramEngagement: instagramEngagementData,
          facebookEngagement: facebookEngagementData
        });

        console.log('Processed analytics data:', {
          instagram: instagramData,
          facebook: facebookData,
          instagramEngagement: instagramEngagementData,
          facebookEngagement: facebookEngagementData
        });

        setError(null);
      } catch (err) {
        console.error('Error fetching analytics:', err);
        setError('Failed to load analytics data. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchAnalytics();
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-lg font-medium text-gray-600">Loading analytics data...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded relative" role="alert">
        <strong className="font-bold">Error!</strong>
        <span className="block sm:inline"> {error}</span>
      </div>
    );
  }

  // Process engagement data for Instagram
  const engagementByHour = analyticsData.instagramEngagement?.engagement_by_hour || {};
  const engagementLabels = Object.keys(engagementByHour).sort((a, b) => parseInt(a) - parseInt(b));

  const engagementData = engagementLabels.map(hour => {
    const values = engagementByHour[hour] || [];
    const avg = values.length > 0 
      ? values.reduce((sum, val) => sum + val, 0) / values.length 
      : 0;
    return avg.toFixed(2);
  });

  // Process best posting times data
  const bestTimesData = analyticsData.instagramEngagement?.best_times || {};
  const bestTimesLabels = Object.keys(bestTimesData).sort((a, b) => parseInt(a) - parseInt(b));
  const bestTimesValues = bestTimesLabels.map(hour => {
    const value = bestTimesData[hour]?.avg_engagement || 0;
    return parseFloat(value.toFixed(2));
  });

  // Prepare chart data
  const engagementChartData = {
    labels: engagementLabels.map(hour => `${hour}:00`),
    datasets: [
      {
        label: 'Engagement Rate by Hour',
        data: engagementData,
        borderColor: 'rgb(75, 192, 192)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        tension: 0.3
      },
    ],
  };

  const bestTimesChartData = {
    labels: bestTimesLabels.map(hour => `${hour}:00`),
    datasets: [
      {
        label: 'Best Times to Post',
        data: bestTimesValues,
        backgroundColor: 'rgba(54, 162, 235, 0.8)',
      },
    ],
  };

  // Platform distribution data
  const platformData = {
    labels: ['Instagram', 'Facebook'],
    datasets: [
      {
        data: [
          analyticsData.instagram?.total_engagement || 0,
          analyticsData.facebook?.total_engagement || 0,
        ],
        backgroundColor: ['#E4405F', '#1877F2'],
      },
    ],
  };

  // Extract summary stats with safer access
  const instagramFollowers = analyticsData.instagram?.total_followers_gained || 
                           analyticsData.instagram?.follower_trend?.counts?.[analyticsData.instagram?.follower_trend?.counts?.length - 1] || 0;

  const instagramEngagementRate = analyticsData.instagramEngagement?.avg_engagement_rate || 
                                analyticsData.instagram?.avg_engagement_rate || 0;

  const instagramTotalEngagement = analyticsData.instagram?.total_engagement || 0;

  const facebookFollowers = analyticsData.facebook?.total_followers_gained || 
                          analyticsData.facebook?.growth_metrics?.total_followers_gained || 0;

  const facebookEngagementRate = analyticsData.facebook?.engagement_rate || 
                              analyticsData.facebook?.growth_metrics?.engagement_rate || 0;

  const facebookTotalEngagement = analyticsData.facebook?.total_engagement || 
                                analyticsData.facebook?.growth_metrics?.total_engagement || 0;

  return (
    <div className="analytics space-y-8">
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold text-gray-900">Analytics Dashboard</h1>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="card">
          <h2 className="text-xl font-bold mb-4">Instagram Performance</h2>
          <div className="grid grid-cols-3 gap-4">
            <div className="stat-card">
              <div className="stat-value">{instagramFollowers}</div>
              <div className="stat-label">Followers Gained</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{instagramEngagementRate.toFixed(2)}%</div>
              <div className="stat-label">Engagement Rate</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{instagramTotalEngagement}</div>
              <div className="stat-label">Total Engagement</div>
            </div>
          </div>
        </div>

        <div className="card">
          <h2 className="text-xl font-bold mb-4">Facebook Performance</h2>
          <div className="grid grid-cols-3 gap-4">
            <div className="stat-card">
              <div className="stat-value">{facebookFollowers}</div>
              <div className="stat-label">Followers Gained</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{facebookEngagementRate.toFixed(2)}%</div>
              <div className="stat-label">Engagement Rate</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{facebookTotalEngagement}</div>
              <div className="stat-label">Total Engagement</div>
            </div>
          </div>
        </div>
      </div>

      <div className="analytics-grid">
        <div className="chart-container">
          <h3>Engagement Rate by Hour</h3>
          {engagementLabels.length > 0 ? (
            <Line data={engagementChartData} options={{
              responsive: true,
              plugins: {
                legend: {
                  position: 'top',
                },
                tooltip: {
                  callbacks: {
                    label: function(context) {
                      return `Engagement: ${context.parsed.y}%`;
                    }
                  }
                }
              },
              scales: {
                y: {
                  beginAtZero: true,
                  title: {
                    display: true,
                    text: 'Engagement Rate (%)'
                  }
                },
                x: {
                  title: {
                    display: true,
                    text: 'Hour of Day'
                  }
                }
              }
            }} />
          ) : (
            <div className="text-center py-10 text-gray-500">No data available</div>
          )}
        </div>

        <div className="chart-container">
          <h3>Best Times to Post</h3>
          {bestTimesLabels.length > 0 ? (
            <Bar data={bestTimesChartData} options={{
              responsive: true,
              plugins: {
                legend: {
                  position: 'top',
                },
                tooltip: {
                  callbacks: {
                    label: function(context) {
                      return `Score: ${context.parsed.y}`;
                    }
                  }
                }
              },
              scales: {
                y: {
                  beginAtZero: true,
                  title: {
                    display: true,
                    text: 'Engagement Score'
                  }
                },
                x: {
                  title: {
                    display: true,
                    text: 'Hour of Day'
                  }
                }
              }
            }} />
          ) : (
            <div className="text-center py-10 text-gray-500">No data available</div>
          )}
        </div>

        <div className="chart-container">
          <h3>Engagement by Platform</h3>
          <div className="flex justify-center" style={{ height: '250px' }}>
            <Pie data={platformData} options={{
              responsive: true,
              maintainAspectRatio: false,
              plugins: {
                legend: {
                  position: 'bottom',
                },
                tooltip: {
                  callbacks: {
                    label: function(context) {
                      const label = context.label || '';
                      const value = context.parsed || 0;
                      return `${label}: ${value} engagements`;
                    }
                  }
                }
              }
            }} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Analytics;
